<?phpnamespace App\Models\Items;use App\Lib\Model;use App\Traits\LapBootTrait;class ItemAttributeValue extends Model{	use LapBootTrait;    /**     * @return \Illuminate\Database\Eloquent\Relations\BelongsTo     */	public function language()	{		return $this->belongsTo(\App\Models\Language::class);	}    /**     * @return \Illuminate\Database\Eloquent\Relations\BelongsToMany     */	public function itemAttributeType()	{		return $this->belongsTo(ItemAttributeType::class);	}    /**     * @return \Illuminate\Database\Eloquent\Relations\BelongsToMany     */	public function items()    {        return $this->belongsToMany(Item::class);    }}