INSERT INTO `products` (`id`, `code`, `name`, `brand_id`, `description`, `price`, `weight`, `category_id`) VALUES
(1, 'OCC.C1', 'COLAR CERVICAL MACIO C1 MEDI', 1, '<h3>Indicações:</h3>\n<p>Síndrome cervical, hipermobilidade, problemas reumáticos, entorses excessivas nos músculos do pescoço, lesões de golpe de chicote, alterações degenerativas da coluna vertebral.</p>\n\n<h3>Função: </h3>\n<p>Estabilização da coluna vertebral, apoio, aquecimento e limitação do movimento.</p>\n\n<h3>Características: </h3>\n<p>Formato anatómico, material confortável em espuma de média densidade, tecido 100% algodão, disponível em três alturas para cada tamanho.</p>\n\n<b>Fonte medi</b>', '10.00', 150, 1),
(2, 'OCC.C1O', 'COLAR CERVICAL MACIO C1 ORTHIA', 2, '<h3>Indicações: </h3>\n<p>Pós-traumatismos, torcicolos, artrites, artroses, contraturas da coluna vertebral, desabituação gradual após uso de colar rígido, uso noturno.</p>\n\n<h3>Função: </h3>\n<p>Imobilizam parcialmente a região cervical evitando movimentos bruscos e estabilizando a postura.</p>\n\n<h3>Características: </h3>\n<p>Formato anatómico, fabricado em espuma de poliuretano de média densidade, com forro 100% algodão e são fornecidos com uma forra adicional amovível e lavável, com a mesma composição.</p>', '13.65', 150, 1),
(3, '7301', 'COLAR CERVICAL MACIO JUZOPRO', 3, '<h3>Indicações: </h3>\n<p>Tratamento de situações de dor na coluna cervical, golpe de chicote, alterações relacionadas com cansaço e desgaste, síndroma cervical, torcicolo, dor pós-traumática, osteocondrose e espondilartrose.</p>\n\n<h3>Função: </h3>\n<p>Apoio da coluna cervical. Graças à sua forma anatómica, os colares cervicais JuzoPro adaptam-se de forma ótima ao queixo, pescoço e ombros, podendo também ser usados durante a noite.</p>\n\n<h3>Características: </h3>\n<p>Com fecho com velcro, perfeitamente ajustável, com revestimento macio hipoalergénico e respirável, o que permite um uso confortável durante mais tempo. 100% espuma.</p>', '30.90', 150, 1),
(4, 'OCC.C2', 'COLAR CERVICAL SEMI RÍGIDO C2 MEDI', 1, '<h3>Indicações: </h3>\n<p>Síndrome cervical, hipermobilidade, problemas reumáticos, entorses excessivas nos músculos do pescoço, lesões de golpe de chicote, alterações degenerativas da coluna vertebral. </p>\n\n<h3>Função: </h3>\n<p>Estabilização da coluna vertebral, apoio, aquecimento e limitação do movimento. </p>\n\n<h3>Características: </h3>\n<p>Formato anatómico, material confortável em espuma de média densidade com barra de estabilização adicional, tecido 100% algodão, disponível em três alturas para cada tamanho. </p>\n\n<b>Fonte medi</b>', '14.00', 150, 1),
(5, 'OCC.C2O', 'COLAR CERVICAL SEMI RÍGIDO C2 ORTHIA', 2, '<h3>Indicações:</h3>\n<p>Pós-traumatismos, nevralgia cérvico-braquial, entorse cervical.</p> \n\n<h3>Função: </h3>\n<p>Imobilizam parcialmente a região cervical evitando movimentos bruscos e estabilizando a postura.</p> \n\n<h3>Características: </h3> \n<p>Formato anatómico, fabricado em espuma de poliuretano de média densidade com placa de polipropileno que fixa no exterior através de velcro, o que confere maior conforto na utilização. Ao retirar a placa, o colar passa a ter a funcionalidade de colar cervical macio. Com forro 100% algodão e são fornecidos com uma forra adicional amovível e lavável, com a mesma composição.</p>', '16.45', 150, 1),
(6, '7302', ' COLAR CERVICAL SEMI RÍGIDO JUZOPRO', 3, '<h3>Indicações: </h3> \n<p>Tratamento de situações de dor na coluna cervical, golpe de chicote, alterações relacionadas com cansaço e desgaste, síndroma cervical, torcicolo, dor pós-traumática, osteocondrose e espondilartrose.</p>\n\n<h3>Função: </h3> \n<p>Este colar integra um elemento de reforço frontal, sendo utilizado nos casos de queixas mais acentuadas ou de problemas mais graves da coluna cervical, proporcionando um apoio mais firme do pescoço. Apoia todo o contorno do maxilar inferior, proporcionando uma sustentação mais eficaz.</p>\n\n<h3>Características: </h3> \n<p>Com fecho com velcro, perfeitamente ajustável, com revestimento macio hipoalergénico e respirável, o que permite um uso confortável durante mais tempo. 100% espuma e componente têxtil mais endurecido integrado.</b>', '32.45', 150, 1),
(7, 'CLL300', 'COLAR CERVICAL RÍGIDO MEDI', 1, '<h3>Indicações:</h3>\n<p>Nevralgia cervical, cervicalgias, torcicolos, entorses nos músculos do pescoço, lesões de golpe de chicote e alterações degenerativas ca coluna vertebral. </p>\n\n<h3>Função:</h3>\n<p>Estabiliza a coluna vertebral, limita o movimento e exerce função de apoio e imobilização.</p>\n\n<h3>Características:</h3>\n<p>Fabricado em polipropileno de baixa densidade e forrado nos rebordos superior e inferior. Altura regulável por velcros.</p>\n\n<b>Fonte medi</b>', '22.15', 300, 1),
(8, 'P900', 'COLAR CERVICAL PHILADÉLFIA', 1, '<h3>Indicações:</h3>\n<p>Traumatismo agudo da cervical. Fraturas simples e estáveis, utilização pós-operatória após luxações, doenças degenerativas, imobilização após trauma ou cirurgia, estenose da coluna vertebral e espondilolistese.</p>\n\n<h3>Função:</h3>\n<p>Imobiliza toda a coluna cervical, suportando as zonas occipital e mentonianas para uma imobilização mais eficaz. Restringe a flexão cervical, extensão e rotação. Com reforço anterior e posterior, mantendo um alinhamento neutro da coluna cervical.</p>\n\n<h3>Características:</h3>\n<p>Fabricado em plastozote e polipropileno de alta densidade nos apoios occipital e mentoniano. O colar cervical é aberto em duas partes garantindo um conforto extra ao utilizador. Com fechos de velcro ajustáveis bilaterais para assegurar um ajuste correto. Abertura traqueal para acesso rápido a traqueotomias de urgência e a monotonização do pulso carotídeo. As aberturas de ventilação reduzem o calor e humidade. Resistente à água para o banho e hidroterapia. Muito leve, indicado para Raio X, tomografia e ressonância magnética. Facilmente personalizado para as necessidades individuais, 3 tamanhos ajustáveis de quatro alturas de cada.</p> \n\n<b>Fonte medi</b>', '33.70', 160, 1),
(9, 'MCD100', 'COLAR CERVICAL DORSAL MINERVA', 1, '<h3>Indicações:</h3>\n<p>Neoplasias cervicais, fraturas ou após fraturas nas vértebras cervicais, pós-operatório. </p>\n\n<h3>Características: </h3>\n<p>Fabricado em plastozote reforçado com Kydex. Concebido com orifício para traqueotomia, de colocação fácil graças ao fecho com tira de velcro.</p>\n\n<b>Fonte medi</b>', '304.20', 600, 1),
(10, 'IMB200', 'SUPORTE DE BRAÇO COM BANDA TORÁCICA MEDI', 1, '<h3>Indicações:</h3> \n<p>Contusões e distensões do ombro, deslocações do ombro, lesões do manguito rotador, fraturas subcapitais estáveis do úmero e fraturas escapulares. </p>\n\n<h3>Função: </h3> \n<p>AFixação estável do ombro e do braço, estabilização adicional graças à faixa peitoral. </p>\n\n<h3>Características: </h3>\n<p>Garante a examinação da ferida, possibilidade de exercícios limitados nas fases posteriores de tratamento graças à remoção fácil do suporte de ombro. </p>\n\n<b>Fonte medi</b>', '44.60', 350, 2),
(11, 'OB.202', 'SUPORTE DE BRAÇO COM BANDA TORÁCICA CONTORNO', 4, '<h3>Indicações: </h3>\n<p>Fraturas ou distensões musculares do braço e ombro. </p>\n\n<h3>Função: </h3>\n<p>Fixação estável do ombro e do braço, estabilização adicional graças à faixa peitoral. </p>\n\n<h3>Características: </h3>\n<p>Possui bolsa com duas tiras que cruzam nas costas e fixam à frente sobre a bolsa, permitindo regular a altura do braço. A faixa torácica permite uma imobilização total do braço. </p>', '30.60', 350, 2),
(12, 'RDS100', 'IMOBILIZADOR CLAVICULAR MEDI', 1, '<h3>Indicações:</h3> \n<p>Distorções e fraturas da clavícula e subluxações, terapia conservativa da fratura traumática da clavícula. </p>\n\n<h3>Função: </h3>\n<p>Imobilização clavicular. O elevado conforto possibilita uma posição cómoda das costas.</p>\n\n<h3>Características: </h3>\n<p>Fabricado em tecido confortável com ajuste de velcro na zona posterior. </p>\n\n<b>Fonte medi</b>', '39.00', 350, 2),
(13, 'MEDI SAK', 'SUPORTE COM ABDUÇÃO VARIÁVEL PARA OMBRO 10 A 60º MEDI', 1, '<h3>Indicações: </h3>\n<p>Síndrome de colisão do ombro em inflamações agudas ou crónicas e após descompressão aberta ou artroscópica e acromioplastia. Reconstrução após ruturas da coifa do rotador, implante de prótese do ombro, ombro congelado após anestesia. </p>\n\n<h3>Função: </h3>\n<p>Imobilização da articulação do ombro com uma abdução de 10º a 60º. Alívio temporário da pressão no espaço subacromial, mobilização opcional sem peso da articulação do ombro no plano horizontal, mobilização para prevenir adesões nos espaços subacromial, subcaracóide e subdeltóide, cunha do braço proporciona proteção adicional para a articulação do ombro.</p>\n\n<h3>Características: </h3>\n<p>Imobilização da articulação do ombro com uma abdução de 10º a 60º. Acolchoado e leve para melhor conforto, fixação segura e adequada ao paciente com tiras do antebraço e braço, apoio confortável tipo bola de ténis para a mão. </p>\n\n<b>Fonte medi<b/>', '177.53', 350, 2),
(14, 'MEDI SAS 15', 'TALA COM ABDUÇÃO 15º PARA O OMBRO MEDI ', 1, '<h3>Indicações: </h3>\n<p>Síndrome de colisão do ombro em inflamações agudas ou crónicas e após descompressão aberta ou artroscópica e acromioplastia, reconstrução após ruturas da coifa do rotador, tratamento conservativo ou pós-operatório após bursite subacromial, relaxamento do limbo ventral e dos componentes capsulares após estabilização aberta e artroscópica do ombro, imobilização conservativa de luxações/deslocamentos do ombro. </p>\n\n<h3>Função: </h3>\n<p>Posicionamento lateral com amortecimento justo e anatomicamente moldado para aliviar o limbo ventral e a cápsula, posição com abdução de 15º para evitar adesões ao recesso axilar (ombro congelado). Imobilização imediata pós-operatória, ativa e passiva da mão, pulso e articulação do ombro, abrindo a tala do antebraço sem retirar toda a faixa. Tratamento de acompanhamento funcional inicial, facilita a fisioterapia pós-operatória.</p>\n\n<h3>Características: </h3>\n<p>Aperto alternativo da tala para ser usada à direita ou à esquerda, apoio confortável para a mão, leve e acolchoado para melhor conforto. </p>\n\n<b>Fonte medi</b>', '140.71', 350, 2),
(15, 'PROTECT.SA 15', 'IMOBILIZADOR DE OMBRO MEDI', 1, '<h3>Indicações: </h3>\n<p>Deslocações do ombro, imobilização da articulação crómio-clavicular, entorses, lesões da coifa do rotador, imobilização de fraturas no braço.</p>\n\n<h3>Função: </h3>\n<p>Alívio da articulação do ombro, úmero e cotovelo. </p>\n\n<h3>Características: </h3>\n<p>Ajuste Universal, adaptação simples e rápido. </p>\n\n<b>Fonte medi</b>', '75.82', 350, 2),
(16, 'MEDI SAS45', 'TALA COM ABDUÇÃO 45º OU 30º PARA O OMBRO MEDI', 1, '<h3>Indicações: </h3>\n<p>Reconstrução após ruturas da coifa do rotador, imobilização após implantação de prótese do ombro, tratamento conservativo ou pós-operatório após bursite subacromial, ruturas parciais da coifa do rotador, após acromioplastia, relaxamento do limbo ventral e dos componentes capsulares após estabilização aberta e artroscópica do ombro, imobilização conservativa de luxações/deslocamento do ombro. </p>\n\n<h3>Função: </h3>\n<p>Posicionamento lateral com amortecimento ajustado e anatomicamente moldado para aliviar o limbo ventral e a cápsula, posição de abdução a 30º ou 45º para evitar adesões ao recesso axilar (ombro congelado), fixação segura da articulação do ombro, graças a almofada de braço adicional. Mobilização pós-operatória, ativa e passiva da mão, pulso e articulação do cotovelo, recuperação funcional fácil, melhora a fisioterapia pós-operatória.</p>\n\n<h3>Características: </h3>\n<p>AO aperto universal permite usar a tala à direita ou à esquerda, fácil de colocar, alteração opcional de amortecimento a 30º para 45º e apoio confortável para a mão. </p>\n\n<b>Fonte medi</b>', '155.84', 350, 2),
(17, 'MEDI SAS MULTI', 'SUPORTE COM ABDUÇÃO 15º MEDI', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós operatória de ruturas da coifa do rotador, fraturas da cabeça do úmero, implantes prostéticos do ombro, imobilização após deslocamento posterior do ombro. </p>\n\n<h3>Função: </h3>\n<p>Alivia a pressão no espaço subcromial, suporta o limbro ventral e as áreas capsulares e previne a adesão do prolongamento axilar.</p>\n\n<h3>Características: </h3>\n<p>Suporte fixo do braço, apoio para a mão auxilia nos exercícios do antebraço. </p>\n\n<b>Fonte medi</b>', '165.20', 350, 2),
(18, 'OMOMED', 'SUPORTE DE OMBRO COM FUNÇÃO DE LIMITAÇÃO DE ROTAÇÃO MEDI', 1, '<h3>Indicações: </h3>\n<p>Fraturas proximais da cabeça do úmero, da cavidade glenóide e da escápula e luxação articular, tratamento conservativo para osteoartrite dolorosa ou deslocação anterior crónica ou aguda do ombro, ligamentoplastia do ombro ou após estabilização ligamentar do ombro, reconstrução das inserções tendinosas dos músculos da coifa de rotadores e refixação dos tendões do bicepete (lesão SLAP). </p>\n\n<h3>Função: </h3>\n<p>Limitação e prevenção de movimentos prejudiciais graças à limitação marcada e ajustável de rotação externa, abdução e flexão anterior da articulação do ombro. A malha elástica exerce uma compressão local ligeira nos tecidos moles superficiais para limitar e impedir o inchaço e reduzir a tensão muscular. O tónus reduzido dos músculos em volta do ombro corrige os défices posturais no ombro com uma melhor centralização da cabeça do úmero na cavidade glenóide, e\ntambém alivia a dor no ombro.</p>\n\n<h3>Características: </h3>\n<p>A faixa flexível permite o ajuste e a limitação variável de movimentos da articulação do ombro.</p> \n\n<b>Fonte medi</b>', '67.86', 350, 2),
(19, 'MEDI SLK90', 'SUPORTE ROTATIVO EXTERNO (ÂNGULO DE 90º) MEDI', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós-operatória, utilização conservativa após lesão de Bankart reservada, instabilidade anterior e posterior bidirecional do ombro ou após libertação da cápsula, fraturas do tuberculum majus sem deslocamento, transferência do latissimus dorsi. </p>\n\n<h3>Função: </h3>\n<p>Permite o posicionamento do ombro a 90º para o plano frontal, libertando a cápsula posterior para uma posição ótima.</p>\n\n<h3>Características: </h3>\n<p>Mobilização pós-operatória imediata, passiva e ativa da mão, punho e cotovelo, almofada praticamente anti deslizante, mesmo na posição deitada. </p>\n\n<b>Fonte medi</b>', '157.56', 350, 2),
(20, 'MEDI ARM FIX', 'SUPORTE ROTATIVO INTERNO (ABDUÇÃO 0º) MEDI', 1, '<h3>Indicações: </h3>\n<p>Tratamento conservativo após luxação e subluxação crónica do ombro anterior, fraturas proximais da cabeça do úmero, fraturas da escápula, luxação de articulação, síndrome de colisão do ombro e artrose de articulação, tratamento conservativo para osteoartrite dolorosa. Contra indicações: Tratamento conservativo e pós-operatório de luxação de ombro posterior.</p>\n\n<h3>Função: </h3>\n<p>ZImobilização praticamente completa do ombro e das articulações do cotovelo, alívio de dor tanto na terapia conservativa como após procedimentos cirúrgicos. Correção de posições incorretas com centralização da cabeça do úmero na cavidade da articulação do ombro.</p>\n\n<h3>Características: </h3>\n<p>Pode ser aplicado pelo paciente apenas com uma mão. </p>\n\n<b>Fonte medi</b>', '46.64', 350, 2),
(21, 'MEDI ARM SLING', 'SUPORTE ROTATIVO INTERNO MEDI', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós operatória, deslocamento do ombro. </p>\n\n<h3>Função: </h3>\n<p>Alívio da articulação do ombro, úmero e cotovelo. Fixa o braço junto ao corpo e, deste modo, imobiliza o ombro.</p>\n\n<h3>Características: </h3>\n<p>Com manga para polegar, para maior conforto, adaptação e colocação simples e rápida. </p>\n\n<b>Fonte medi</b>', '43.52', 350, 2),
(22, 'MEDI EASY SLING', 'BANDA DE SUPORTE E IMOBILIZAÇÃO DO OMBRO', 1, '<h3>Indicações: </h3>\n<p>Pós-operatório após sutura do manguito rotador, após estabilização do ombro, após reconstrução acrómio-clavicular, após prótese do ombro, após osteossíntese e tratamento conservativo de fraturas.</p>\n\n<h3>Função: </h3> \n<p>Imobiliza a articulação glenoumeral no caso de grande variabilidade do grau de imobilização. De dia: mobilidade controlada. De noite: fixação rígida.</p>\n\n<h3>Características: </h3>\n<p>Aplicação simples, construção aberta durante o dia e apoio seguro durante a noite. Possibilidade de apoio para mulheres com seios grandes e em casos de dor na nuca.</p>\n\n<b>Fonte medi</b>', '37.45', 350, 2),
(23, 'PROTECT.SIS', 'IMOBILIZADOR DE OMBRO MEDI', 1, '<h3>Indicações: </h3>\n<p>Deslocação do ombro, imobilização da articulação crómio-articular, entorses, lesões do manguito rotador, imobilização de fraturas do braço. </p>\n\n<h3>Função: </h3>\n<p>Alívio da articulação do ombro, úmero e cotovelo. </p>\n\n<h3>Características: </h3>\n<p>Ajuste e adaptação simples e rápido.</p>\n\n<b>Fonte medi</b>', '39.00', 350, 2),
(24, 'RGB100', 'SUPORTE DE BRAÇO SIMPLES MEDI', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós-operatória, deslocação do ombro. </p>\n\n<h3>Função: </h3> \n<p>Faz sustentação do braço para possibilitar a recuperação. </p>\n\n<h3>Características: </h3>\n<p>Fabricado em algodão, ajuste e adaptação simples e rápido. </p>\n\n<b>Fonte medi</b>', '15.45', 100, 3),
(25, 'OB.SBO', 'SUPORTE DE BRAÇO SIMPLES ORTHIA', 2, '<h3>Indicações: </h3>\n<p>Imobilização pós operatória, pós colocação de gesso, luxações ou subluxações. </p>\n\n<h3>Função: </h3>\n<p>Faz sustentação do braço para possibilitar a recuperação. </p>\n\n<h3>Características: </h3>\n<p>Possui apoio do polegar para prevenir lesões do punho e uma alça ajustável para regular a altura do braço. </p>', '17.50', 120, 3),
(26, 'ELBOW SUPPORT', 'COTOVELEIRA ELÁSTICA SIMPLES MEDI', 1, '<h3>Indicações: </h3>\n<p>Inchaços e efusões de articulações, irritações reumáticas e de osteoartrite. </p>\n\n<h3>Função: </h3>\n<p>Alivia a articulação irritada e melhora o fluxo sanguíneo. </p>\n\n<h3>Características: </h3>\n<p>Suporte extensível em malha dupla de formato anatómico e altamente elástico. </p>\n\n<b>Fonte medi</b>', '11.85', 120, 3),
(27, 'EPICOMED', 'COTOVELEIRA COM ALMOFADAS EM SILICONE MEDI', 1, '<h3>Indicações: </h3>\n<p>Epicondilites laterais e centrais (cotovelo de tenista e golfista), deslocação do cotovelo, efusão e inchaço devido a artrite e osteoartrite, inflamação pós traumática e pós operatória crónica.</p> \n\n<h3>Função: </h3>\n<p>O tecido em malha de compressão e a inserção em silicone exercem um efeito de massagem que promove o processo de cura. Áreas de pressão específicas para reduzir a dor e aliviar a tensão nas inserções do tendão.</p>\n\n<h3>Características: </h3>\n<p>Tira amovível para ajuste da pressão individual, malha anatomicamente moldada com almofadas texturadas em silicone. </p>\n\n<b>Fonte medi</b>', '64.45', 120, 3),
(28, 'ELBOW STRAP', 'BANDA PNEUMÁTICA PARA COTOVELO MEDI', 1, '<h3>Indicações: </h3>\n<p>Epicondilite (cotovelo de tenista e golfista. Função: A pressão da banda reduz a tensão nos tendões. </p>\n\n<h3>Função: </h3>\n<p>A pressão da banda reduz a tensão nos tendões.</p>\n\n<h3>Características: </h3>\n<p>Almofada de pressão pneumática, fabricado em material de licra e algodão. </p>\n\n<b>Fonte medi</b>', '24.35', 80, 3),
(29, 'EPI.STRAP', 'BANDA PARA COTOVELO MEDI', 1, '<h3>Indicações: </h3>\n<p>Para reduzir a tensão nos tendões. </p>\n\n<h3>Características: </h3>\n<p>Almofada de compressão confortável. </p>\n\n<b>Fonte medi</b>', '21.53', 80, 3),
(30, 'EPIBRACE', 'BANDA PARA EPICONDILITE MEDI', 1, '<h3>Indicações: </h3>\n<p>Epicondilite (cotovelo de tenista e golfista). </p>\n\n<h3>Função: </h3>\n<p>A almofada pressiona a musculatura do braço, reduzindo o esforço no músculo inflamado e as ligações do tendão no cotovelo. </p>\n\n<h3>Características: </h3>\n<p>A almofada insuflável garante uma pressão contínua sobre toda a área e evita picos de pressão. A almofada é móvel. </p>\n\n<b>Fonte medi</b>', '30.42', 80, 3),
(31, '39', 'BANDA PARA EPICONDILITE ORTHIA', 2, '<h3>Indicações: </h3>\n<p>Epicondilite do radio ou mediano e tendinites. Pode ser usada como medida preventiva durante a atividade desportiva e/ou na fase de reabilitação. </p>\n\n<h3>Função: </h3>\n<p>A almofada interior alivia a dor, diminuindo a tensão dos músculos e ligamentos. </p>\n\n<h3>Características: </h3>\n<p>Compressão rapidamente ajustável. Fácil aplicação e regulação. Respirável e com interior em 100% algodão.</p>', '23.93', 80, 3),
(32, 'EPICOROM', 'ORTÓTESE PARA COTOVELO COM REG. FLEXÃO/EXTENSÃO', 1, '<h3>Indicações: </h3>\n<p>Deslocações e luxações do cotovelo, fraturas de epicondilites centrais e laterais, implantação de próteses, fraturas estáveis do úmero discal, artrólise do cotovelo. </p>\n\n<h3>Função: </h3>\n<p>Imobilização do cotovelo em incrementos de 10º. Imobilização do cotovelo com limitação opcional de movimentos em passos de 10º. Flexão: de 0º-120º; Extensão: de 0-120º.</p>\n\n<h3>Características: </h3>\n<p>Fácil colocação, ajuste e limpeza. Limitação opcional de movimentos em passos de 10º: flexão e extensão de 0º-120º.</p>\n\n<b>Fonte medi</b>', '246.65', 250, 3),
(33, 'PLL200', 'TALA IMOBILIZADORA DE PULSO MEDI', 1, '<h3>Indicações: </h3>\n<p>Lesão crónica do tecido mole do pulso, paralisia do nervo radial, distensões ligeiras a moderadas, tendinite e/ou tenosinovite, artrite reumatóide, após remoção do gesso. </p>\n\n<h3>Função: </h3>\n<p>Imobilização e estabilização do pulso. </p>\n\n<h3>Características: </h3>\n<p>Faixa de suporte larga para o pulso, tala amovível em alumínio, fabricado em material de licra e algodão robusto. </p>\n\n<b>Fonte medi</b>', '32.90', 80, 4),
(34, 'MANUMED', 'SUPORTE PARA PULSO COM TALA IMOBILIZADORA MEDI', 1, '<h3>Indicações: </h3>\n<p>Lesão crónica do tecido mole do pulso, paralisia do nervo radial, imobilizações e distorções, tendinite e/ou tenosinovite, artrite reumatóide. </p>\n\n<h3>Função: </h3>\n<p>Imobilização e estabilização do pulso graças à tala moldável em alumínio. </p>\n\n<h3>Características: </h3>\n<p>Faixa de suporte circular ajustável para o pulso, tala amovível e moldável em alumínio, fabricado em material respirável e anti humidade. </p>\n\n<b>Fonte medi</b>', '52.42', 80, 4),
(35, '33', 'IMOBILIZADOR DE PUNHO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Patologias traumáticas, distorções, tendinites, rizartrose, síndrome do túnel cárpico, osteoartrose, artrose, artrite úrica aguda e artrite reumatóide. </p>\n\n<h3>Função: </h3>\n<p>Suporte do músculo tendinoso pós trauma e pós cirurgia. </p>\n\n<h3>Características: </h3>\n<p>Tecnologia de fecho inovadora, com três talas anatómicas moldáveis que garantem o bloqueio completo da articulação. Prático, confortável, ambidestro, com interior de 100% algodão. </p>', '39.55', 80, 4),
(36, 'MANUMED T', 'TALA PARA PULSO E POLEGAR MEDI', 1, '<h3>Indicações: </h3>\n<p>Distensão ou distorção do pulso e/ou polegar, fixação pós operatória do punho e do polegar, lesão do ligamento colateral ulnar da articulação basal do polegar, tendinite e tendinopatia do pulso e do polegar, fases inflamatórias de doenças reumáticas. </p>\n\n<h3>Função: </h3>\n<p>Fixação e estabilização do pulso, da articulação selar e basal do polegar.</p>\n\n<h3>Características: </h3>\n<p>Tecido não extensível, tala anatómica moldável em alumínio com suporte para polegar. </p>\n\n<b>Fonte medi</b>', '49.90', 80, 4),
(37, '34', 'IMOBILIZADOR DE POLEGAR E PUNHO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Patologias traumáticas, distorções, fraturas do metacarpo, tendinite dos tendões flexores e extensores do polegar, contusões por esmagamento, artrose, rizartrose, síndrome do túnel cárpico com irradiação do polegar, osteoartrose, artrose e artrite reumatóide. </p>\n\n<h3>Função: </h3>\n<p>Suporte do tendão do músculo pós trauma e pós cirurgia aberta da primeira falange do polegar.</p> \n\n<h3>Características: </h3>\n<p>Tecnologia de fecho inovadora, com três talas anatómicas moldáveis que garantem o bloqueio completo da articulação. Prático, confortável, ambidestro, com interior de 100% algodão.</p>', '44.80', 80, 4),
(38, 'MEDI CTS', 'SUPORTE PARA PULSO COM TALA PARA DEDOS MEDI', 1, '<h3>Indicações: </h3>\n<p>Síndrome do túnel cárpico, imobilização do pulso e dos dedos. </p>\n\n<h3>Função: </h3>\n<p>Redução do inchaço, inflamação e imobilização do pulso e dos dedos. </p>\n\n<h3>Características: </h3>\n<p>Confortável e leve, envolve a parte inferior do braço até às articulações distais dos dedos, com tala palmar pré formada, ambidestro. </p>\n\n<b>Fonte medi</b>', '51.95', 80, 4),
(39, 'MANUMED ACTIVE', 'MÃO ELÁSTICA PARA PULSO  COM TALA IMOBILIZADORA MEDI', 1, '<h3>Indicações: </h3>\n<p>Entorses, distensões, osteoartrite do pulso, instabilidade, tenosinovite. </p>\n\n<h3>Função: </h3>\n<p>A tala incorporada estabiliza e reduz o esforço do pulso. </p>\n\n<h3>Características: </h3>\n<p>Tala anatomicamente pré formada com tamanho optimizado que proporciona um suporte máximo, mesmo em mãos grandes. </p>\n\n<b>Fonte medi</b>', '49.00', 80, 4),
(40, 'PLL100', 'TALA IMOBILIZADOR DO POLEGAR MEDI', 1, '<h3>Indicações: </h3>\n<p>Lesões das articulações carpo-metacarpais e metacarpo-falangeais do polegar, debilidade ligamentosa do polegar, artrose da articulação carpo-metacarpal do polegar, artrite reumatóide. </p>\n\n<h3>Função: </h3>\n<p>Imobilização do polegar. </p>\n\n<h3>Características: </h3>\n<p>Fabricado em licra e algodão, com tala moldável em alumínio. </p>\n\n<b>Fonte medi</b>', '24.00', 80, 4),
(41, 'RHIZOMED', 'TALA RÍGIDA DO POLEGAR EM ALUMÍNIO MEDI', 1, '<h3>Indicações: </h3>\n<p>Lesões dos ligamentos laterais da articulação do polegar (polegar de esquiador), irritação das articulações do polegar (por ex: rizartrose) e distorções. </p>\n\n<h3>Função: </h3>\n<p>Estabilização das articulações do polegar. </p>\n\n<h3>Características: </h3>\n<p>Ajustável individualmente graças à construção em alumínio, de fácil colocação.</p>\n\n<b>Fonte medi</b>', '46.50', 80, 4),
(42, '35', 'IMOBILIZADOR DO POLEGAR PAVIS', 5, '<h3>Indicações: </h3>\n<p>Patologias traumáticas, distorções, fraturas do carpo e metacarpo, tendinite, rizartrose, síndrome do túnel cárpico e artrose. </p>\n\n<h3>Função: </h3>\n<p>Imobiliza o polegar por dentro, deixando a parte dorsal livre. </p>\n\n<h3>Características: </h3>\n<p>Luva de proteção que pode ser completamente aberta, de fácil colocação e regulação. Prático, confortável, ambidestro, com interior de 100% algodão. </p>', '31.76', 80, 4),
(43, '32', 'PULSO DE CONTENÇÃO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Tendinites e bursites, traumas menores e danos secundários do pulso, ideal para a prática desportiva. </p>\n\n<h3>Características: </h3>\n<p>Compressão rapidamente ajustável, com interior de 100% algodão, extremamente confortável. Cor: Azul</p>', '18.00', 80, 4),
(44, '30', 'PULSO DE CONTENÇÃO TAMANHO ÚNICO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Tendinites e bursites, traumas menores e danos secundários do pulso, ideal para a prática desportiva. </p>\n\n<h3>Características: </h3>\n<p>Compressão rapidamente ajustável, com interior de 100% algodão, extremamente confortável.</p>', '13.00', 80, 4),
(45, 'LUMBAMED ACTIVE', 'CINTA LOMBAR LUMBAMED ACTIVE MEDI', 1, '<h3>Indicações: </h3>\n<p>Dor lombar inferior, irritação de pequena articulação intervertebral, distúrbios ligamentosos, osteocondrose e espondilose, síndrome pós discectomia, irritação da articulação sacroilíaca. </p>\n\n<h3>Função: </h3>\n<p>Ligeira diminuição da lordose e da rotação pélvica anterior, 4 barras moldáveis integradas dorsalmente estabilizam a região lombo-sagrada, a compressão propriocetiva estimula o metabolismo, reduz a tensão muscular e alivia a dor.</p>\n\n<h3>Características: </h3>\n<p>Com 4 barras moldáveis em alumínio, tecido elástico e respirável, sustentação forte. </p>\n\n<b>Fonte medi</b>', '57.90', 350, 5),
(46, '700FV', 'CINTA LOMBAR PROTPOINT', 6, '<h3>Indicações: </h3>\n<p>Faixa lombo-sacro, indicada para lesões gerais da coluna lombar tais como lombalgias agudas e crónicas, tratamentos pré e pós operatórios. </p>\n\n<h3>Função: </h3>\n<p>Termoterapia local e compressão. </p>\n\n<h3>Características: </h3>\n<p>Compressão ajustável, com seis varetas flexíveis, com painel de neoprene extraível para proporcionar melhor proteção e calor na zona lombar. Altura 25cm. Deve-se medir a cintura para escolher o tamanho adequado.</p>', '44.45', 350, 5),
(47, 'LUMBAMED BASIC S', 'CINTA LOMBAR LUMBAMED BASIC SENHORA MEDI', 1, '<h3>Indicações: </h3>\n<p>Dor lombar inferior, desequilíbrio do músculo postural na coluna lombar, distúrbios ligamentosos, osteocondrose e espondilose, síndrome pós discectomia, irritação da articulação sacro-ilíaca. </p>\n\n<h3>Função: </h3>\n<p>Diminui ligeiramente a lordose e a rotação pélvica anterior, alívio evidente para a espinha lombar e suporte para os músculos das costas. As 4 barras dorsais moldáveis, integradas e com formato anatómico podem ser ajustadas manualmente de modo a garantir a adaptação perfeita à anatomia da região lombo-sagrada. O efeito de compressão propriocetiva exercida pelo suporte estimula o metabolismo, reduz a tensão muscular e alivia a dor.</p>\n\n<h3>Características: </h3>\n<p>Com 4 barras dorsais moldáveis, forrada a tecido clima comfort respirável. </p>\n\n<p>Modelo para senhora.</p> \n\n<b>Fonte medi</b>', '72.40', 350, 5),
(48, 'LUMBAMED BASIC H', 'CINTA LOMBAR LUMBAMED BASIC HOMEM MEDI', 1, '<h3>Indicações: </h3>\n<p>Dor lombar inferior, desequilíbrio do músculo postural na coluna lombar, distúrbios ligamentosos, osteocondrose e espondilose, síndrome pós discectomia, irritação da articulação sacro-ilíaca. </p>\n\n<h3>Função: </h3>\n<p>Diminui ligeiramente a lordose e a rotação pélvica anterior, alívio evidente para a espinha lombar e suporte para os músculos das costas. As 4 barras dorsais moldáveis, integradas e com formato anatómico podem ser ajustadas manualmente de modo a garantir a adaptação perfeita à anatomia da região lombo-sagrada. O efeito de compressão propriocetiva exercida pelo suporte estimula o metabolismo, reduz a tensão muscular e alivia a dor.</p>\n\n<h3>Características: </h3>\n<p>Com 4 barras dorsais moldáveis, forrada a tecido clima comfort respirável. </p>\n\n<p>Modelo para Homem. </p>\n\n<b>Fonte medi</b>', '72.40', 350, 5),
(49, 'LUMBAMED PLUS S', 'CINTA LOMBAR LUMBAMED PLUS SENHORA MEDI', 1, '<h3>Indicações: </h3>\n<p>Dor lombar inferior, desequilíbrio do músculo postural na coluna lombar, distúrbios ligamentosos, osteocondrose e espondilose, síndrome pós discectomia, irritação da articulação sacroilíaca, sinfisiolise. </p>\n\n<h3>Função: </h3>\n<p>Diminui ligeiramente a lordose e a rotação pélvica anterior, a almofada Vario-Flex estabiliza a articulação sacroilíaca e alivia a articulação lombo-sagrada. Massagem dos músculos lombares para melhorar a circulação, graças à almofada e malha compressora, estimulação do metabolismo e redução da tensão muscular e da dor.</p>\n\n<h3>Características: </h3>\n<p>Com 4 barras dorsais moldáveis, forrada a tecido clima comfort respirável, com almofada vario-flex que estabiliza a articulação sacroilíaca e promove a massagem dos músculos lombares para melhor circulação e alívio da dor. </p>\n\n<p>Modelo para senhora. </p>\n\n<b>Fonte medi</b>', '87.70', 350, 5),
(50, 'LUMBAMED PLUS H', 'CINTA LOMBAR LUMBAMED PLUS HOMEM MEDI', 1, '<h3>Indicações: </h3>\n<p>Dor lombar inferior, desequilíbrio do músculo postural na coluna lombar, distúrbios ligamentosos, osteocondrose e espondilose, síndrome pós discectomia, irritação da articulação sacroilíaca, sinfisiólise. </p>\n\n<h3>Função: </h3>\n<p>Diminui ligeiramente a lordose e a rotação pélvica anterior, a almofada Vario-Flex estabiliza a articulação sacroilíaca e alivia a articulação lombo-sagrada. Massagem dos músculos lombares para melhorar a circulação, graças à almofada e malha compressora, estimulação do metabolismo e redução da tensão muscular e da dor.</p>\n\n<h3>Características: </h3>\n<p>Com 4 barras dorsais moldáveis, forrada a tecido clima comfort respirável, com almofada vario-flex que estabiliza a articulação sacroilíaca e promove a massagem dos músculos lombares para melhor circulação e alívio da dor. </p>\n\n<p>Modelo para Homem. </p>\n\n<b>Fonte medi</b>', '87.70', 350, 5),
(51, '705FV', 'LOMBOSTATO PROTPOINT', 6, '<h3>Indicações: </h3>\n<p>Faixa lombostato, indicada para lesões gerais da coluna lombar tais como lombalgias agudas e crónicas, tratamentos pré e pós-operatórios. </p>\n\n<h3>Função: </h3>\n<p>Termoterapia local e compressão. </p>\n\n<h3>Características: </h3>\n<p>Compressão ajustável, com seis varetas flexíveis, com painel de neoprene extraível para proporcionar melhor proteção e calor na zona lombar. Altura 30cm. Deve-se medir a cintura para escolher o tamanho adequado.</p>', '53.05', 350, 5),
(52, 'LUMBAMED DISC S', 'CINTA LOMBAR COM ESTABILIZAÇÃO VARIÁVEL SENHORA MEDI', 1, '<h3>Indicações: </h3>\n<p>Lumbago, alterações degenerativas, osteocondrose, espondiloartrose, dor lombar inferior com ciática, espondilose sem deslizamento vertebral, espondilólise menor, espondilolistese, protrusão do disco intervertebral ou saliências do disco. Modelo para senhora com corte anatómico. </p>\n\n<h3>Função: </h3>\n<p>Alivia a pressão nos segmentos espinais afetados reduzindo o tónus muscular, nivela a lordose lombar (ligação da lordose lombar), suporta a área abdominal (ativa os músculos abdominais), diminui a rotação pélvica anterior. Graças ao design modular (almofada lombar e barras estabilizadoras substituíveis com dois graus de rigidez diferentes), a estabilidade do suporte pode ser adaptada/reduzida de acordo com a progressão da recuperação. A ativação necessária dos músculos do tronco previne a perda de massa muscular.</p>\n\n<h3>Características: </h3>\n<p>O tecido Clima Comfort respirável afasta a humidade, proporciona um conforto permanente. Comfort Zone: o suporte em malha anatómica garante um ajuste perfeito sem pressão – principalmente devido ao sistema de aperto com formato anatómico. Fácil de colocar graças às pegas de velcro manuais.</p>\n\n<p>Modelo para senhora. </p>\n\n<b>Fonte medi</b>', '153.82', 350, 5),
(53, 'LUMBAMED DISC H', 'CINTA LOMBAR COM ESTABILIZAÇÃO VARIÁVEL HOMEM MEDI', 1, '<h3>Indicações: </h3>\n<p>Lumbago, alterações degenerativas, osteocondrose, espondiloartrose, dor lombar inferior com ciática, espondilose sem deslizamento vertebral, espondilólise menor, espondilolistese, protrusão do disco intervertebral ou saliências do disco. Modelo para homem com corte reto. </p>\n\n<h3>Função: </h3>\n<p>Alivia a pressão nos segmentos espinais afetados reduzindo o tónus muscular, nivela a lordose lombar (ligação da lordose lombar), suporta a área abdominal (ativa os músculos abdominais), diminui a rotação pélvica anterior. Graças ao design modular (almofada lombar e barras estabilizadoras substituíveis com dois graus de rigidez diferentes), a estabilidade do suporte pode ser adaptada/reduzida de acordo com a progressão da recuperação. A ativação necessária dos músculos do tronco previne a perda de massa muscular.</p>\n\n<h3>Características: </h3>\n<p>O tecido Clima Comfort respirável afasta a humidade, proporciona um conforto permanente. Comfort Zone: o suporte em malha anatómica garante um ajuste perfeito sem pressão – principalmente devido ao sistema de aperto com formato anatómico. Fácil de colocar graças às pegas de velcro manuais.</p>\n\n<p>Modelo para homem. </p>\n\n<b>Fonte medi</b>', '153.82', 350, 5),
(54, 'LUMBAMED FACET S', 'ORTÓTESE LOMBAR SENHORA LUMBAMED FACET', 1, '<h3>Indicações: </h3>\n<p>Artrose das articulações facetarias, protusão e prolapso dos discos intervertebrais, lombalgias/ciatalgias graves, lumbago, sacroileíte, dores crónicas na região lombossacral, espondilose sem deslizamento de vértebras, espondilolistese, desequilíbrio muscular, instabilidade segmentar, estenose do canal espinal lombar.</p>\n\n<p>Modelo para senhora.</p>\n\n<b>Fonte medi</b>', '123.25', 350, 5),
(55, 'LUMBAMED FACET H', 'ORTÓTESE LOMBAR HOMEM LUMBAMED FACET', 1, '<h3>Indicações: </h3>\n<p>Artrose das articulações facetarias, protusão e prolapso dos discos intervertebrais, lombalgias/ciatalgias graves, lumbago, sacroileíte, dores crónicas na região lombossacral, espondilose sem deslizamento de vértebras, espondilolistese, desequilíbrio muscular, instabilidade segmentar, estenose do canal espinal lombar.</p>\n\n<p>Modelo para homem.</p>\n\n<b>Fonte medi</b>', '123.25', 350, 5),
(56, 'PROTECT.LUMBAFORTE', 'CINTA LOMBAR LUMBAFORTE MEDI', 1, '<h3>Indicações: </h3>\n<p>Insuficiência miostática e muscular, irritação das pequenas articulações intervertebrais, sinfisiólise, lesão de ligamentos, dores na região lombo-sagrada, apoio após discectomia. </p>\n\n<h3>Função: </h3>\n<p>Reduz significativamente a carga na coluna lombar, apoia os músculos das costas e alivia as dores na zona inferior das costas e os espasmos musculares. </p>\n\n<h3>Características: </h3>\n<p>Estabilização e alívio através de elementos de suporte adicionais.</p>\n\n<b>Fonte medi</b>', '39.50', 350, 5),
(57, 'PROTECT.LUMBOSTYLE_21', 'CINTA LOMBAR LUMBOSTYLE 21cm MEDI', 1, '<h3>Indicações: </h3>\n<p>Tratamento de condições dolorosas da coluna lombar, ciática lombar, espondilose, espondilolistese, protuberância discal, osteocondrose, espondiloartrose. </p>\n\n<h3>Função: </h3>\n<p>Compressão controlada aplicada através do material e das talas estabilizadoras integradas, apoio dos músculos do tronco, alivia entorses na coluna lombar e alivia dores.</p>\n\n<h3>Características: </h3>\n<p>Estabilização e alívio, faixas de tensão adicionais. </p>\n\n<b>Fonte medi</b>', '46.50', 350, 5),
(58, 'PROTECT.LUMBOSTYLE_26', 'CINTA LOMBAR LUMBOSTYLE 26cm MEDI', 1, '<h3>Indicações: </h3>\n<p>Tratamento de condições dolorosas da coluna lombar, ciática lombar, espondilose, espondilolistese, protuberância discal, osteocondrose, espondiloartrose. </p>\n\n<h3>Função: </h3>\n<p>Compressão controlada aplicada através do material e das talas estabilizadoras integradas, apoio dos músculos do tronco, alivia entorses na coluna lombar e alivia dores.</p>\n\n<h3>Características: </h3>\n<p>Estabilização e alívio, faixas de tensão adicionais. </p>\n\n<b>Fonte medi</b>', '53.35', 0, 5),
(59, 'LUMBAMED STABIL_26', 'CINTA LOMBAR COM FAIXAS DE TENSÃO LUMBAMED STABIL', 1, '<h3>Indicações: </h3>\n<p>Lumbago, alterações degenerativas, osteocondrose, espondiloartrose, dor lombar inferior com ciática, espondilose sem deslizamento vertebral, espondilólise menor, espondilolistese, protrusão do disco intervertebral ou saliências do disco. </p>\n\n<h3>Função: </h3>\n<p>4 barras lombares manualmente adaptáveis e anatomicamente pré-moldadas estabilizam a área da coluna lombar, as faixas de tensão eficazes suportam a correção da postura espinal através da compressão individualmente ajustável e do suporte da cavidade abdominal. As fixações das faixas nas costas nivelam de modo eficaz a lordose (evitando o efeito de configuração de lordose da faixa central sobre as costas).</p>\n\n<h3>Características: </h3>\n<p>Uma possibilidade de escolha entre duas alturas nas costas, 26 e 32cm garante a adaptação ótima a diferentes tamanhos corporais. </p>\n\n<b>Fonte medi</b>', '85.80', 350, 5),
(60, 'LUMBAMED STABIL_32', 'CINTA LOMBAR COM FAIXAS DE TENSÃO LUMBAMED STABIL', 1, '<h3>Indicações: </h3>\n<p>Lumbago, alterações degenerativas, osteocondrose, espondiloartrose, dor lombar inferior com ciática, espondilose sem deslizamento vertebral, espondilólise menor, espondilolistese, protrusão do disco intervertebral ou saliências do disco. </p>\n\n<h3>Função: </h3>\n<p>4 barras lombares manualmente adaptáveis e anatomicamente pré-moldadas estabilizam a área da coluna lombar, as faixas de tensão eficazes suportam a correção da postura espinal através da compressão individualmente ajustável e do suporte da cavidade abdominal. As fixações das faixas nas costas nivelam de modo eficaz a lordose (evitando o efeito de configuração de lordose da faixa central sobre as costas).</p>\n\n<h3>Características: </h3>\n<p>Uma possibilidade de escolha entre duas alturas nas costas, 26 e 32cm garante a adaptação ótima a diferentes tamanhos corporais. </p>\n\n<b>Fonte medi</b>', '95.16', 0, 5),
(61, 'PROTECT.DORSOFIX S', 'CORPETE TORACO LOMBAR SEMI RÍGIDO SENHORA MEDI', 1, '<h3>Indicações: </h3>\n<p>Dores agudas e crónicas, recuperação de fratura vertebral, radiculite lombar, hérnias discais, artrose vertebral, curvatura da coluna torácica, espondilolistese ligeira, discectomia. </p>\n\n<h3>Função: </h3>\n<p>Ajusta-se perfeitamente às formas anatómicas do corpo, proporcionando máxima estabilidade do segmento toracolombar.</p>\n\n<h3>Características: </h3>\n<p>Material inovador de licra e algodão, extremamente confortável, equipado com tiras de ombros, bandas, fechos em velcro, faixas elásticas e um, reforço frontal em polietileno, design exclusivo para adaptação à curvatura da coluna torácica. </p>\n\n<p>Modelo para senhora. </p>\n\n<b>Fonte medi</b>', '148.20', 350, 5),
(62, 'PROTECT.DORSOFIX H', 'CORPETE TORACO LOMBAR SEMI RÍGIDO HOMEM MEDI', 1, '<h3>Indicações: </h3>\n<p>Dores agudas e crónicas, recuperação de fratura vertebral, radiculite lombar, hérnias discais, artrose vertebral, curvatura da coluna torácica, espondilolistese ligeira, discectomia. </p>\n\n<h3>Função: </h3>\n<p>Ajusta-se perfeitamente às formas anatómicas do corpo, proporcionando máxima estabilidade do segmento toracolombar.</p>\n\n<h3>Características: </h3>\n<p>Material inovador de licra e algodão, extremamente confortável, equipado com tiras de ombros, bandas, fechos em velcro, faixas elásticas e um, reforço frontal em polietileno, design exclusivo para adaptação à curvatura da coluna torácica. </p>\n\n<p>Modelo para homem. </p>\n\n<b>Fonte medi</b>', '148.20', 350, 5),
(63, '707FV', 'COLETE DORSO-LOMBAR PROTPOINT', 6, '<h3>Indicações: </h3>\n<p>Faixa dorso-lombar, indicada para lesões gerais da coluna lombar, dorsal, lordoses e cifoses, lombalgias agudas e crónicas, tratamentos pré e pós operatórios. </p>\n\n<h3>Função: </h3>\n<p>Termoterapia local e compressão. </p>\n\n<h3>Características: </h3>\n<p>Compressão ajustável, com seis varetas flexíveis, com painel de neopreno extraível para proporcionar melhor proteção e calor na zona lombar. Deve-se medir a cintura para escolher o tamanho adequado.</p>', '75.65', 350, 5),
(64, 'SPINOMED.II', 'ORTÓTESE SPINOMED II MEDI', 1, '<h3>Indicações: </h3>\n<p>Colapso ósseo osteoporótico nas zonas lombar e torácica, doença juvenil de Scheuermann, híper-cifose com dores crónicas nas costas. </p>\n\n<h3>Função: </h3>\n<p>Ao ativar os músculos das costas, o suporte fortalece a coluna dorsal de modo a diminuir a cifose osteoporótica, estimula a própria energia muscular do corpo. Sempre que estiver bem colocado, o suporte alivia a dor do paciente e melhora a mobilidade. </p>\n\n<h3>Características: </h3>\n<p>A ortótese facilmente dobrável atrás permite ajustar o suporte individualmente para cada paciente, é leve e praticamente invisível por baixo do vestuário, o princípio de “mochila” facilita a colocação da ortótese. Sem qualquer obstrução da respiração torácica ou abdominal, sem restrições dos movimentos dos ombros e dos braços. A ortótese é moldável a frio.</p>\n\n<b>Fonte medi</b>', '221.70', 400, 5),
(65, 'SPINOMED IV', 'ORTÓTESE SPINOMED IV MEDI', 1, '<h3>Indicações: </h3>\n<p>Fratura osteoporótica de vértebras da coluna vertebral e/ou lombar, doença juvenil de Scheuermann, cifose com dores crónicas nas costas. </p>\n\n<h3>Função: </h3>\n<p>De formato ergonómico, pode ser ajustado individualmente à curvatura das costas. </p>\n\n<h3>Características: </h3>\n<p>Graças às alças com formato ergonómico para os ombros, a ortótese é tão fácil de colocar como uma mochila. Leve, design moderno das alças.</p>\n\n<b>Fonte medi</b>', '247.40', 400, 5),
(66, 'MEDI 3C', 'ORTÓTESE DE HIPEREXTENSÃO CRUCIFORME MEDI', 1, '<h3>Indicações: </h3>\n<p>Fraturas de compressão estável das zonas vertebrais torácica e lombar centrais e inferiores, híper-cifose na doença de Scheuermann, terapia conservativa das vértebras, fraturas não operáveis nas zonas lombar e torácica, cuidados ortóticos pós operatórios. </p>\n\n<h3>Função: </h3>\n<p>Extensão da coluna. </p>\n\n<h3>Características: </h3>\n<p>Fecho de cremalheira novo e patenteado, simples e fácil de usar, esforço reduzido do processo de fecho, o apoio do esterno permite a adaptação dinâmica aos movimentos corporais, elevado conforto.</p>\n\n<b>Fonte medi</b>', '237.75', 700, 5),
(67, 'MEDI 4C', 'ORTÓTESE DE HIPEREXTENSÃO COM APOIOS SUBCLAVICULARES MEDI', 1, '<h3>Indicações: </h3>\n<p>Fraturas de compressão estável das zonas vertebrais torácica e lombar centrais e inferiores, imobilização após cirurgia de discos intervertebrais, estabilização axial em ossos osteoporóticos colapsados da coluna lombar e torácica, cuidados ortóticos após cirurgia a tumores e metástases dorsais inoperáveis, recuperação de cirurgia de descompressão espinal com e sem fixação interna. </p>\n\n<h3>Função: </h3>\n<p>Extensão da coluna (junção torácico lombar)</p>\n\n<h3>Características: </h3>\n<p>Excelente ajuste, fecho rápido novo e patenteado, simples e fácil de usar, aplicação rápida. Tam.I Almofada traseira com 22cm. Tam.II Almofada traseira com 28cm.</p>\n\n<b>Fonte medi</b>', '360.36', 700, 5),
(68, 'MEDI 4CFLEX', 'ORTÓTESE DE HIPEREXTENSÃO BASCULANTE (JEWET) MEDI', 1, '<h3>Indicações: </h3>\n<p>Fraturas de compressão estável das zonas vertebrais torácica e lombar centrais e inferiores, imobilização após cirurgia de discos intervertebrais, estabilização axial em ossos osteoporóticos colapsados da coluna lombar e torácica, cuidados ortóticos após cirurgia a tumores e metástases dorsais inoperáveis, recuperação de cirurgia de descompressão espinal com e sem fixação interna. </p>\n\n<h3>Função: </h3>\n<p>Hiperextensão da coluna vertebral, que permite a estabilização no plano sagital. A estabilidade rotativa é restringida pelas almofadas de extensão subclaviculares, a estrutura estável impede a flexão lateral.</p>\n\n<h3>Características: </h3>\n<p>Barra de anca móvel para maior conforto na posição sentada, excelente ajuste, largura das barras do peito ajustáveis.</p>\n\n<b>Fonte medi</b>', '189.85', 700, 5),
(69, '576', 'CORRETOR POSTURAL PAVIS', 5, '<h3>Indicações: </h3> \n<p>Ombros rígidos ou caídos, má postura raquidiana e/ou escoliose, hipercifose e rotação interna dos ombros. </p>\n\n<h3>Função: </h3>\n<p>Ajuda a manter uma postura correta dos ombros relaxando os músculos peitorais. Previne a rotação interna de ombros, diminuindo a tensão excessiva dos músculos cervicais. </p>\n\n<h3>Características: </h3>\n<p>Acolchoamento na zona das axilas, 100% algodão sobre a pele, antialérgico e respirável.</p>', '45.70', 300, 6),
(70, '674', 'FAIXA ABDOMINAL ALGODÃO 27CM PAVIS', 5, '<h3>Indicações: </h3>\n<p>Prevenção contra o relaxamento dos músculos abdominais, pós-cirurgia ou pós-parto. </p>\n\n<h3>Função: </h3>\n<p>Contenção abdominal. </p>\n\n<h3>Características: </h3>\n<p>Fabricado em algodão puro, elástico, antialérgico e respirável que providencia um excelente conforto e tolerância. Antibacteriano. De fácil aplicação devido ao fecho exclusivo de dupla banda, anatómico. Altura 27cm.</p>', '37.20', 300, 6),
(71, '675_18', 'FAIXA ABDOMINAL/TORÁCICA ALGODÃO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Prevenção contra o relaxamento dos músculos abdominais, pós-cirurgia ou pós-parto. O modelo de 18cm de altura pode ser utilizado como faixa torácica, para cirurgias do foro torácico.</p>\n\n<h3>Função: </h3>\n<p>Contenção abdominal. </p>\n\n<h3>Características: </h3>\n<p>Fabricado em algodão puro, elástico, antialérgico e respirável que providencia um excelente conforto e tolerância. Antibacteriano. De fácil aplicação devido ao fecho exclusivo de dupla banda, anatómico. Alturas disponíveis: 18cm, 24cm e 30cm. O modelo de 18cm de altura tem apenas uma banda de velcro e a medida deve ser tirada ao perímetro do tórax. </p>', '34.75', 300, 6),
(72, '675_24', 'FAIXA ABDOMINAL/TORÁCICA ALGODÃO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Prevenção contra o relaxamento dos músculos abdominais, pós-cirurgia ou pós-parto. O modelo de 18cm de altura pode ser utilizado como faixa torácica, para cirurgias do foro torácico.</p>\n\n<h3>Função: </h3>\n<p>Contenção abdominal. </p>\n\n<h3>Características: </h3>\n<p>Fabricado em algodão puro, elástico, antialérgico e respirável que providencia um excelente conforto e tolerância. Antibacteriano. De fácil aplicação devido ao fecho exclusivo de dupla banda, anatómico. Alturas disponíveis: 18cm, 24cm e 30cm. O modelo de 18cm de altura tem apenas uma banda de velcro e a medida deve ser tirada ao perímetro do tórax. </p>', '36.58', 0, 6),
(73, '675_30', 'FAIXA ABDOMINAL/TORÁCICA ALGODÃO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Prevenção contra o relaxamento dos músculos abdominais, pós-cirurgia ou pós-parto. O modelo de 18cm de altura pode ser utilizado como faixa torácica, para cirurgias do foro torácico.</p>\n\n<h3>Função: </h3>\n<p>Contenção abdominal. </p>\n\n<h3>Características: </h3>\n<p>Fabricado em algodão puro, elástico, antialérgico e respirável que providencia um excelente conforto e tolerância. Antibacteriano. De fácil aplicação devido ao fecho exclusivo de dupla banda, anatómico. Alturas disponíveis: 18cm, 24cm e 30cm. O modelo de 18cm de altura tem apenas uma banda de velcro e a medida deve ser tirada ao perímetro do tórax. </p>', '39.10', 0, 6),
(74, '810', 'FAIXA ABDOMINAL TAMANHO ÚNICO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Prevenção contra o relaxamento dos músculos abdominais, pós-cirurgia ou pós-parto. </p>\n\n<h3>Função: </h3>\n<p>Contenção abdominal. </p>\n\n<h3>Características: </h3>\n<p>De fácil aplicação devido ao fecho exclusivo de dupla banda, tamanho único graças ao seu tecido elástico exclusivo com velcros. Altura 27cm. </p>', '29.00', 300, 6);
INSERT INTO `products` (`id`, `code`, `name`, `brand_id`, `description`, `price`, `weight`, `category_id`) VALUES
(75, '673', 'CINTA DE CONTENÇÃO ABDOMINAL LETIZIA PAVIS', 5, '<h3>Indicações: </h3>\n<p>Pós intervenções cirúrgicas e pós-parto. </p>\n\n<h3>Função: </h3>\n<p>Oferece uma eficaz sustentação e favorece a recuperação da linha estética. Previne e combate o relaxamento abdominal. Pode ser usada diariamente para moldar o corpo. Graças à sua força de contenção que atua com resultados máximos, estas cintas vestem perfeitamente e proporcionam conforto. </p>\n\n<h3>Características: </h3>\n<p>Tecido com reforço duplo elástico na zona abdominal, 100% algodão sobre a pele, antialérgico e respirável. </p>\n\n<b>Não se efetuam trocas de roupa interior.</b>', '50.30', 300, 6),
(76, '671', 'CINTA DE CONTENÇÃO ABDOMINAL  SILVY PAVIS', 5, '<h3>Indicações: </h3>\n<p>Pós intervenções cirúrgicas e pós-parto. </p>\n\n<h3>Função: </h3>\n<p>Oferece uma eficaz sustentação, previne e combate os relaxamentos abdominais, ptoses viscerais e renais. Esta cinta oferece a recuperação da linha estética. </p>\n\n<h3>Características: </h3>\n<p>Abertura fácil através dos colchetes. Tecido com reforço duplo elástico na zona abdominal, 100% algodão sobre a pele, antialérgico e respirável. </p>\n\n<b>Não se efetuam trocas de roupa interior.</b>', '57.40', 300, 6),
(77, '672', 'CINTA DE CONTENÇÃO DE HÉRNIAS  MULHER PAVIS', 5, '<h3>Indicações: </h3> \n<p>Contenção de hérnias inguinais ou após cirurgias. Preparado para utilização opcional de pelotas.</p> \n\n<h3>Função: </h3>\n<p>Evita os relaxamentos abdominais e previne ptoses viscerais e renais. Pelotas adquiridas à parte.</p> \n\n<h3>Características: </h3>\n<p>Abertura fácil através de colchetes. Tecido com reforço duplo elástico na zona abdominal e inguinal, 100% algodão sobre a pele, antialérgico e respirável. </p>\n\n<b>Não se efetuam trocas de roupa interior.</b>', '61.75', 300, 6),
(78, '655', 'BOXER DE CONTENÇÃO DE HÉRNIAS HOMEM PAVIS', 5, '<h3>Indicações: </h3>\n<p>Contenção e suporte da parede abdominal para evitar flacidez e/ou relaxamento dos tecidos e para conter hérnias inguinais redutíveis. </p>\n\n<h3>Função: </h3>\n<p>Oferece uma contenção ampla, macia e muito flexível direcionada em linha oblíqua em relação à cintura para uma contenção na zona inguinal mais eficaz. Preparado para utilização opcional de pelotas. </p>\n\n<h3>Características: </h3>\n<p>Tecido com reforço duplo elástico na zona abdominal e inguinal. </p>\n\n<b>Não se efetuam trocas de roupa interior.</b>', '56.10', 300, 6),
(79, 'OTCE.PEL', 'PELOTA PARA CINTAS INGUINAIS PAVIS', 5, '<h3>Indicações: </h3>\n<p>Contenção da hérnia inguinal. </p>\n\n<h3>Características: </h3>\n<p>Dispositivo que é inserido no interior dos bóxeres e slips de contenção. Disponível em duas versões: Direita e esquerda.</p>', '5.15', 50, 6),
(80, 'MEDI HIP ORTHOSIS', 'ORTÓTESE FUNCIONAL DA ANCA C/ AJUSTE FLEX/EXT ABDUÇÃO/ADUÇÃO', 1, '<h3>Indicações: </h3>\n<p>Estabilização pós operatória após recolocação total inicial da anca e após cirurgia de revisão da anca, após resseção da cabeça do fémur, após osteotomia de correção, descarga operatória. </p>\n\n<h3>Função: </h3>\n<p>Protege contra deslocamento/luxação, terapia funcional controlada inicial com exercícios, limitações de extensão e flexão definidas em incrementos de 10º e até 120º.</p>\n\n<h3>Características: </h3>\n<p>Definições infinitamente variáveis para o ângulo de abdução, suportes de construção pélvica e para coxa fixos e anatomicamente moldados, circunferência e altura ajustáveis, acolchoado tipo turco muito confortável, ajuste perfeito graças a peças separadas para anca e coxa, fácil e rápido de tirar e colocar.</p>\n\n<b>Fonte medi</b>', '504.50', 1200, 7),
(81, 'MEDI ORTHOCOX', 'DISPOSITIVO ELÁSTICO PÓS OPERATÓRIO DA ANCA', 1, '<h3>Indicações: </h3>\n<p>Osteotomia de reposição intertroncantérica, fixação interna de fraturas transtrocantéricas, substituição total da anca. </p>\n\n<h3>Função: </h3>\n<p>Controla a rotação da anca, compressão, auxilia na mobilização do paciente, acesso fácil ao penso.</p> \n\n<h3>Características: </h3>\n<p>Aplicação simples e rápida, utilização muito confortável, impermeável, hipoalergénico.</p>\n\n<b>Fonte medi</b>', '77.20', 700, 7),
(82, '601', 'JOELHEIRA ELÁSTICA SIMPLES MEDI', 1, '<h3>Indicações: </h3>\n<p>Inflamações ou distensões do joelho, incluindo pós artroscopia e cirurgia, distensões de músculos e ligamentos, bursite, artrite, osteoartrite. </p>\n\n<h3>Função: </h3> \n<p>Alivia a articulação irritada e melhora a circulação sanguínea. </p>\n\n<h3>Características: </h3>\n<p>Fabricada em material leve e respirável, confortável. </p>\n\n<b>Fonte medi</b>', '14.00', 250, 8),
(83, '20', 'JOELHEIRA ELÁSTICA REGULÁVEL PAVIS', 5, '<h3>Indicações: </h3>\n<p>Derrames articulares e traumas rotulianos, reabilitação avançada de patologias rotulianas. </p>\n\n<h3>Função: </h3>\n<p>Ação contentora pós-traumática, fácil ajustamento superior ou inferior à rótula, dependendo do grau de compressão desejado. </p>\n\n<h3>Características: </h3>\n<p>Ambidestro, respirável, antialérgico, 100% algodão sobre a pele.</p>', '45.50', 250, 8),
(84, '605', 'JOELHEIRA ELÁSTICA RÓTULA ABERTA MEDI', 1, '<h3>Indicações: </h3>\n<p>Inflamações ou distensões do joelho, incluindo pós artroscopia e cirurgia, distensões de músculos e ligamentos, bursite, artrite, osteoartrite. </p>\n\n<h3>Função: </h3>\n<p>Alivia a articulação irritada e melhora a circulação sanguínea. </p>\n\n<h3>Características: </h3>\n<p>Fabricada em material leve e respirável, confortável, com rótula aberta e duas espirais em aço (flexíveis) mediais laterais. </p>\n\n<b>Fonte medi</b>', '37.00', 250, 8),
(85, 'GENUMEDI', 'JOELHEIRA GENUMEDI (RÓTULA ABERTA) MEDI', 1, '<h3>Indicações: </h3>\n<p>Instabilidade, efusões e inchaços articulares, inflamação pós operatória e traumática crónica, condropatia patelar, osteoartrite e artrite, lesões do menisco, síndrome de dor patelo-femoral.</p> \n\n<h3>Função: </h3>\n<p>O efeito de massagem alcançado pelo tecido de compressão e a inserção de silicone melhora a circulação sanguínea e leva a uma reabsorção mais rápida de hematomas e edemas. A joelheira sem costuras e moldada melhora a proprioceção e, deste modo, melhora o suporte muscular (estabilização do joelho).</p>\n\n<h3>Características: </h3>\n<p>Anel de silicone especialmente concebido para envolver a rótula, sistema anti deslizamento com silicone texturado a nível medial e lateral, alívio máximo de pressão para a rótula graças ao forro especial do anel de silicone. Cor: Cinza, Areia e Preto (apenas na versão Genumedi normal).</p>\n\n<b>Fonte medi</b>', '40.85', 250, 8),
(86, 'GENUMEDI PLUS', 'JOELHEIRA GENUMEDI PLUS (RÓTULA ABERTA) MEDI', 1, '<h3>Indicações: </h3>\n<p>Instabilidade, efusões e inchaços articulares, inflamação pós operatória e traumática crónica, condropatia patelar, osteoartrite e artrite, lesões do menisco, síndrome de dor patelo-femoral.</p> \n\n<h3>Função: </h3>\n<p>O efeito de massagem alcançado pelo tecido de compressão e a inserção de silicone melhora a circulação sanguínea e leva a uma reabsorção mais rápida de hematomas e edemas. A joelheira sem costuras e moldada melhora a proprioceção e, deste modo, melhora o suporte muscular (estabilização do joelho).</p>\n\n<h3>Características: </h3>\n<p>Anel de silicone especialmente concebido para envolver a rótula, sistema anti deslizamento com silicone texturado a nível medial e lateral, alívio máximo de pressão para a rótula graças ao forro especial do anel de silicone, com tiras adicionais para estabilização. </p>\n\n<b>Fonte medi</b>', '45.85', 250, 8),
(87, '23', 'JOELHEIRA ELÁSTICA RÓTULA ABERTA PAVIS', 5, '<h3>Indicações: </h3>\n<p>Derrames articulares subluxação da rótula, instabilidade, tendinites e trauma do menisco. </p>\n\n<h3>Função: </h3>\n<p>Ação contentora pós traumática, reabilitação avançada de patologias rotulianas e meniscais, proteção nas atividades desportivas. </p>\n\n<h3>Características: </h3>\n<p>Fácil colocação, as duas hastes espirais muito flexíveis permitem o livre movimento da articulação, com buraco rotuliano que promove a estabilidade sem pressão na região poplítea, ambidestra, 100% algodão sobre a pele, antialérgico. </p>', '38.15', 250, 8),
(88, 'GENUMEDI PT', 'JOELHEIRA ELÁSTICA PARA TRAÇÃO DA RÓTULA MEDI', 1, '<h3>Indicações: </h3>\n<p>Síndrome doloroso patelofemoral em casos de rótula hipermóvel, lateralização da rótula, desequilíbrio muscular, desalinhamento da rótula, tratamento pós-operatório (estabilização da rótula). </p>\n\n<h3>Função: </h3>\n<p>Efeito positivo nas estruturas musculares que orientam a rótula, para compensar o desequilíbrio. Relaxa o músculo vasto lateral e restabelece o equilíbrio muscular, ativa o músculo vasto medial. O anel de silicone reforçado lateralmente, orienta suavemente a rótula para movimentação fiável no sulco femoral. Eficaz compressão propriocetiva para orientação e estabilização fiável das articulações.</p>\n\n<h3>Características: </h3>\n<p>As almofadas posicionadas com precisão, com pontos de fricção neurofisiológicos, relaxam e ativam os músculos vasto lateral e vasto medial, respetivamente. A tira tensora e, consequentemente, a pressão sobre o músculo vasto lateral pode ser ajustada conforme necessário. O anel de silicone é reforçado lateralmente para a inclinação correta da rótula. A tira em silicone texturado e as talas laterais garantem um ajuste ideal e sem pregas, mesmo com os joelhos fletidos. </p>\n\n<b>Fonte medi</b>', '58.35', 250, 8),
(89, 'TTP100', 'BANDA PNEUMÁTICA PARA TENDÃO ROTULIANO MEDI', 1, '<h3>Indicações: </h3> \n<p>Dores associadas ao desalinhamento patelo-femoral, instabilidades moderadas da articulação do joelho. </p>\n\n<h3>Função: </h3>\n<p>A pressão no tendão do quadríceps melhora o desempenho propriocetivo, reforçando o suporte muscular da articulação do joelho. </p>\n\n<h3>Características: </h3>\n<p>Amortecimento anatomicamente moldado, fabricado em material de algodão e licra. </p>\n\n<b>Fonte medi</b>', '24.00', 80, 8),
(90, '45', 'SUPORTE ROTULIANO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Tendinite rotuliana, apophysitis, artralgia femoropatelar, joelho de saltador. </p>\n\n<h3>Função: </h3>\n<p>Alivia a dor diminuindo a tensão dos músculos e ligamentos, pode ser usada na atividade desportiva. </p>\n\n<h3>Características: </h3>\n<p>Compressão ajustável, fácil de aplicar e regular, 100% algodão sobre a pele, ambidestro. </p>', '21.55', 80, 8),
(91, '565', 'JOELHEIRA IMOBILIZADORA A 0º OU 20º (TALA DE DEPUY)', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós-operatória, dor pré-operatória, após deslocação da rótula, após lesão dos ligamentos. </p>\n\n<h3>Função: </h3>\n<p>Imobilização do joelho a 0º ou 20º. </p>\n\n<h3>Características: </h3>\n<p>Ajuste rápido e individual, barra de estabilização medial, lateral e posterior amovíveis, confortável.</p>\n\n<b>Fonte medi</b>', '46.65', 400, 8),
(92, 'MEDI PTS', 'JOELHEIRA IMOBILIZADORA C/ SUPORTE TIBIAL POSTERIOR (TALA DE DEPUY)', 1, '<h3>Indicações: </h3>\n<p>Rutura recente do ligamento cruzado posterior ou instabilidade crónica do mesmo, imobilização pós-operatória após reconstrução do LCP, implantação de prótese da articulação do joelho, dor patelo-femoral resistente a tratamento, dor aguda na zona anterior do joelho. </p>\n\n<h3>Função: </h3>\n<p>Posicionamento da barriga da perna anteriormente em relação à coxa através da almofada da barriga da perna. Tensão reduzida no LCP e menos pressão na articulação patelo-femoral. Imobilização a 0º.</p>\n\n<h3>Características: </h3>\n<p>Ajuste rápido, ajuste individual do comprimento da almofada da barriga da perna, barras de estabilização medial, lateral e posterior amovíveis, material confortável.</p>\n\n<b>Fonte medi</b>', '161.30', 500, 8),
(93, 'PROTECT.PT SOFT', 'JOELHEIRA PARA CORREÇÃO DA LATERALIZAÇÃO DA RÓTULA', 1, '<h3>Indicações: </h3> \n<p>Síndrome doloroso patelo-femoral, condropatia patelar, osteoartrite retropatelar, irritações pós-operatórias e pós-traumáticas, instabilidades da rótula e do joelho. </p>\n\n<h3>Função: </h3>\n<p>Correção da localização lateral errada da rótula graças a almofada viscoelástica de estabilização, joelheira macia com efeito propriocetivo e de compressão. </p>\n\n<h3>Características: </h3>\n<p>Fabricada em material D-Tex II elástico, respirável, almofadas anatomicamente moldadas.</p>\n\n<b>Fonte medi</b>', '70.05', 450, 8),
(94, 'STABIMED', 'JOELHEIRA C/ LIMITAÇÃO FLEX/EXT', 1, '<h3>Indicações: </h3>\n<p>Instabilidade ligeira a moderada da articulação do joelho, lesões do menisco, alterações traumáticas ou degenerativas da articulação do joelho. </p>\n\n<h3>Função: </h3>\n<p>Estabilização colateral da articulação do joelho, prevenção de hiperextensão e limitação da gama de movimentos, efeito compressivo e propriocetivo. </p>\n\n<b>Fonte medi</b>', '159.15', 450, 8),
(95, 'STABIMED PRO', 'JOELHEIRA C/ ARTIC. POLICÊNTRICAS', 1, '<h3>Indicações: </h3>\n<p>Instabilidade ligeira a moderada da articulação do joelho, lesões do menisco, alterações traumáticas ou degenerativas da articulação do joelho. </p>\n\n<h3>Função: </h3>\n<p>Estabilização colateral da articulação do joelho, prevenção de hiperextensão e limitação da gama de movimentos, efeito compressivo e propriocetivo. </p>\n\n<b>Fonte medi</b>', '121.70', 450, 8),
(96, 'COLLAMED', 'JOELHEIRA COM LIMITAÇÃO FLEX/EXT', 1, '<h3>Indicações: </h3>\n<p>Tratamento conservativo de lesões ligamentosas colaterais, instabilidades moderadas dos ligamentos cruzados, lesões do menisco, instabilidade crónica, alterações traumáticas ou degenerativas da articulação do joelho. </p>\n\n<h3>Função: </h3>\n<p>Prevenção de hiperextensão e limitação da gama de movimentos, excelente estabilização colateral, efeiro compressivo e propriocetivo. </p>\n\n<b>Fonte medi</b>', '288.60', 450, 8),
(97, 'MEDI PT CONTROL', 'JOELHEIRA C/ REG. FLEX/EXT E TRAÇÃO', 1, '<h3>Indicações: </h3>\n<p>Síndrome da dor patelo-femoral, condropatia patelar, osteoartrite retro-patelar, irritações pós-operatórias e pós-traumáticas, após cirurgia de libertação lateral para limitar a extensão e flexão da articulação e para estabilizar a rótula. </p>\n\n<h3>Função: </h3>\n<p>Estabilização colateral da articulação do joelho, prevenção de hiperextensão e limitação dos movimentos, efeito compressivo e propriocetivo, estabilização do contraforte da rótula que evita o movimento lateral através da flexão e extensão.</p> \n\n<b>Fonte medi</b>', '269.88', 450, 8),
(98, 'PROTECT.ST', 'JOELHEIRA C/ REG. FLEX/EXTENSÃO', 1, '<h3>Indicações: </h3> \n<p>Instabilidades ligeiras a moderadas da articulação do joelho, lesões do menisco, deformações traumáticas ou degenerativas. </p>\n\n<h3>Função: </h3>\n<p>Estabilização lateral da articulação, prevenção de hiperextensão e limitação da amplitude, efeito compressivo e propriocetivo. </p>\n\n<b>Fonte medi</b>', '154.45', 450, 8),
(99, 'PROTECT.ST PRO', 'JOELHEIRA C/ ARTIC. POLICÊNTRICAS', 1, '<h3>Indicações: </h3>\n<p>Alterações traumáticas ou degenerativas da articulação do joelho, luxação ligeira a moderada dos ligamentos laterais, artrite ligeira, após cirurgia artroscópica do menisco, instabilidades ligeiras da articulação do joelho. </p>\n\n<h3>Função: </h3>\n<p>Estabilização lateral da articulação, prevenção de hiperextensão, efeito propriocetivo e compressivo. Ideal para desportos de contacto. </p>\n\n<b>Fonte medi</b>', '94.85', 450, 8),
(100, 'PROTECT.CO II', 'JOELHEIRA LONGA C/ LIMITAÇÃO FLEX/EXT', 1, '<h3>Indicações: </h3>\n<p>Lesões de ligamentos laterais, instabilidades ligeiras dos ligamentos cruzados, lesões do menisco, instabilidade crónica, deformações traumáticas ou degenerativas da articulação do joelho. </p>\n\n<h3>Função: </h3>\n<p>Efeito de massagem devido à compressão e às almofadas viscoelásticas, melhora a proprioceção. </p>\n\n<b>Fonte medi</b>', '247.10', 450, 8),
(101, 'PROTECT.OA SOFT', 'JOELHEIRA PARA OSTEOARTRITE', 1, '<h3>Indicações: </h3>\n<p>Osteoartrite ligeira a moderada do joelho, medial ou lateral. </p>\n\n<h3>Função: </h3>\n<p>Correção do espaço lateral ou medial da articulação, alívio da dor através da redução do peso no espaço afetado da articulação, diminui nova irritação da cápsula articular, cinta macia propriocetiva e de compressão. </p>\n\n<b>Fonte medi</b>', '234.00', 450, 8),
(102, 'M.4 s', 'JOELHEIRA RÍGIDA C/ ARTICULAÇÃO PARA LIMITAÇÃO FLEX/EXT', 1, '<h3>Indicações: </h3>\n<p>Ruturas do ligamento cruzado posterior ou do ligamento cruzado anterior, isoladas ou com lesões secundárias (ligamento colateral e/ou lesão do menisco), instabilidade grave, inoperável. Rutura complexa do ligamento colateral e/ou rutura cruzada parcial ou estiramento excessivo. Instabilidade grave do ligamento colateral com lesão do menisco. Refixação do menisco em instabilidade pré-existente da articulação do joelho. </p>\n\n<b>Fonte medi</b>', '501.40', 500, 8),
(103, 'M.4 AGR', 'JOELHEIRA RÍGIDA PARA JOELHO RECURVATUM', 1, '<h3>Indicações: </h3>\n<p>Joelho recurvatum.</p> \n\n<h3>Função: </h3>\n<p>O sistema progressivo de absorção reduz o impacto na superfície da articulação do joelho durante a fase de extensão final do caminhar. </p>\n\n<b>Fonte medi</b>', '827.45', 500, 8),
(104, 'M.4 s OA ', 'JOELHEIRA RÍGIDA PARA OSTEOARTRITE', 1, '<h3>Indicações: </h3>\n<p>Tratamento conservativo de instabilidade ligamentosa complexa, especialmente em combinação com osteoartrite unilateral, alívio da pressão pós-operatória e proteção compartimental após fraturas da cabeça tibial, cirurgia de cartilagem, refixação do menisco, osteotomia de correção, instabilidade dos ligamentos cruzados com alívio de pressão do compartimento medial ou lateral. </p>\n\n<b>Fonte medi</b>', '605.60', 500, 8),
(105, 'MEDI ROM  ', 'JOELHEIRA PÓS-OPERATÓRIA COM LIMITAÇÃO FLEX/EXT', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós-operatória, imobilização segura de ligamentos colaterais e/ou cruzados lesionados ou operados.</p> \n\n<h3>Função: </h3>\n<p>Estabilização colateral da articulação do joelho, imobilização pós-operatória, mobilização controlada. </p>\n\n<b>Fonte medi</b>', '184.90', 500, 8),
(106, 'MEDI ROM DELUXE', 'JOELHEIRA PÓS-OPERATÓRIA COM BLOQUEIO EXTENSÃO', 1, '<h3>Indicações: </h3>\n<p>Instabilidade do joelho e/ou lesões neuromusculares do membro inferior, pós-operatório, reconstrução de ligamentos, meniscectomia, terapia conservativa e reabilitação. </p>\n\n<h3>Função: </h3>\n<p>Estabilização colateral da articulação do joelho, imobilização pós-operatória, mobilização controlada. </p>\n\n<b>Fonte medi</b>', '219.80', 500, 8),
(107, 'PROTECT.ROM', 'TALA UNIVERSAL PARA JOELHO C/ LIMITAÇÃO FLEX/EXT ', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós-operatória, estabilização segura de ligamentos laterais ou cruzados lesionados ou pós-operados. </p>\n\n<h3>Função: </h3>\n<p>Estabilização lateral da articulação do joelho, imobilização pós-operatória, mobilização controlada.</p> \n\n<b>Fonte medi</b>', '128.00', 500, 8),
(108, '501', 'PÉ ELÁSTICO SIMPLES MEDI', 1, '<h3>Indicações: </h3>\n<p>Inflamações ou distensões do tornozelo, distensões musculares, contusão, utilização profilática e terapêutica em atividades desportivas. </p>\n\n<h3>Função: </h3>\n<p>Alivia a articulação irritada e melhora a circulação sanguínea até às áreas de tecido mole que envolvem a articulação produzindo uma ação de compressão e massagem constante durante os movimentos do paciente. </p>\n\n<h3>Características: </h3>\n<p>Fabricado em material leve e respirável, confortável, facilita o movimento da articulação.</p> \n\n<b>Fonte medi</b>', '12.20', 80, 9),
(109, '10', 'CONTENÇÃO REGULÁVEL DO TORNOZELO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Hematomas e derrames articulares.</p>\n\n<h3>Função: </h3>\n<p>Compressão homogénea e constante que favorece a reabsorção de líquidos. </p>\n\n<h3>Características: </h3>\n<p>Confortável e prática de usar, pode ser completamente aberta, antialérgico, respirável e 100% algodão sobre a pele, ambidestra.</p>', '22.90', 80, 9),
(110, '15', 'CONTENÇÃO ELÁSTICA DO TORNOZELO COM ENFAIXAMENTO EM \"8\" PAVIS', 5, '<h3>Indicações: </h3>\n<p>Distensões, tendinites, luxações, suporte passivo para recuperação funcional do tornozelo, traumatismos do tornozelo, apoio e proteção na prática desportiva. </p>\n\n<h3>Função: </h3>\n<p>Com banda elástica que é enrolada em volta do tornozelo num formato de \"8\", como forma de limitar a abdução e adução articular. </p>\n\n<h3>Características: </h3>\n<p>Confortável, adapta-se ao pé, podendo ser facilmente utilizado em qualquer tipo de sapato, antialérgico, respirável e 100% algodão sobre a pele, ambidestra.</p>', '45.50', 80, 9),
(111, 'ACHIMED', 'PÉ ELÁSTICO PARA TENDÃO DE AQUILES C/ CUNHA EM SILICONE MEDI', 1, '<h3>Indicações: </h3>\n<p>Inflamações agudas e crónicas do tendão de Aquiles, inflamação pós-traumática e pós-operatória, aquilodinia, aquilobursite. </p>\n\n<h3>Função: </h3>\n<p>O tecido de compressão e a inserção em silicone exercem um efeito de massagem que promove o processo de cura. Acolchoamento protetor macio para o tendão de Aquiles. </p>\n\n<h3>Características: </h3>\n<p>Suporte moldado anatomicamente com almofada de suporte integrada em silicone, com cunhas de calcanhar separadas para reduzir a tensão no tendão de Aquiles (usar em ambos os sapatos ao mesmo tempo para igualar o comprimento das pernas). </p>\n\n<b>Fonte medi</b>', '64.00', 120, 9),
(112, 'LEVAMED', 'SUPORTE DE TORNOZELO COM ALMOFADAS RETRO-MALEOLARES EM SILICONE MEDI', 1, '<h3>Indicações: </h3>\n<p>Ligeira instabilidade da articulação do tornozelo, tornozelo de futebolista, distensões, efusões e inchaços de articulação devido a artrite e osteoartrite, inflamação crónica pós-operatória e pós-traumática.</p> \n\n<h3>Função: </h3> \n<p>O tecido de compressão e as inserções em silicone exercem um efeito de massagem que promove a circulação e, deste modo, leva a uma reabsorção mais rápida de hematomas e edemas. O suporte melhora a proprioceção e, deste modo, alcança um suporte muscular melhor (estabilização do tornozelo). </p>\n\n<h3>Características: </h3>\n<p>Suporte moldado anatomicamente com almofadas retro maleolares em silicone integradas, medial e lateralmente.</p>\n\n<b>Fonte medi</b>', '48.35', 120, 9),
(113, 'LEVAMED ACTIVE', 'PÉ ELÁSTICO COM ALMOFADAS E SISTEMA DE TIRAS', 1, '<h3>Indicações: </h3>\n<p>Instabilidades ligamentares crónicas com traumatismos de supinação recorrentes (entorses), entorse articular com alongamento excessivo da cápsula, na segunda fase do tratamento de lesões do ligamento deltoide, tratamento de acompanhamento de fraturas que não exigem gesso, irritação pós-operatória ou pós-traumática aguda ou crónica das articulações talocrural e subtalar e tratamento conservador de lesões da sindesmose que não requerem cirurgia.</p>\n\n<h3>Função: </h3>\n<p>Efeito de massagem por intermédio de almofadas de silicone integradas, supressão de inchaços (edemas, hematomas). Compressão propriocetiva, orientação e estabilização das articulações.</p>\n\n<h3>Características: </h3>\n<p>Suporte moldado anatomicamente com almofadas retro maleolares em silicone integradas, medial e lateralmente. Sistema de tiras amovíveis e reguláveis individualmente, com zonas elásticas e não-elásticas. Aderência adicional e estabilização “proteção” contra a entorse do tornozelo (supinação).</p>\n\n<b>Fonte medi</b>', '64.15', 120, 9),
(114, 'LEVAMED STABILI TRI', 'ORTÓTESE MODULAR DE TRIPLA AÇÃO MEDI', 1, '<h3>Indicações: </h3>\n<p>Distensões da articulação tibiotársica (grau II-III), instabilidades crónicas da articulação tibiotársica. Cuidados pós-operatórios após cirurgias a ligamentos da articulação tibiotársica. </p>\n\n<h3>Função: </h3>\n<p>Terapia de tripla ação segura da articulação tibiotársica. Na fase aguda imobiliza a articulação, estabiliza-a na mobilização inicial e sustenta-a quando o paciente volta à atividade no final da terapia. Lingueta de bloqueio do astrágalo, faixa estabilizadora e malha de compressão que melhora a proprioceção e estimula a reabsorção rápida de hematomas e edemas. </p>\n\n<b>Fonte medi</b>', '117.00', 250, 9),
(115, 'M.STEP', 'ESTABILIZADOR DE TIBIO-TÁRSICA C/ ALMOFADAS GEL/ESPUMA MEDI', 1, '<h3>Indicações: </h3>\n<p>Cuidados conservativos e pós-operatórios de lesões dos ligamentos fibulares do tornozelo, instabilidades crónicas do tornozelo. </p>\n\n<h3>Função: </h3>\n<p>As talas rígidas asseguram uma boa estabilização em movimentos de pronação e supinação. O gel-espuma da almofada anatómico assegura uma compressão uniforme. </p>\n\n<h3>Características: </h3>\n<p>Tira de calcanhar ajustável, tamanho único, muito leve, acolchoado, ambidestro.</p>\n\n<b>Fonte medi</b>', '53.80', 150, 9),
(116, 'PROTECT.ANKLE AIR', 'ORTÓTESE TORNOZELO C/ TALAS DE ESTAB. LATERAL MEDI', 1, '<h3>Indicações: </h3> \n<p>Cuidados conservativos e pós-operatórios de lesões dos ligamentos fibulares do tornozelo, instabilidades crónicas do tornozelo. </p>\n\n<h3>Função: </h3>\n<p>As talas rígidas asseguram uma boa estabilização em movimentos de pronação e supinação. As almofadas de ar anatómicas asseguram uma compressão uniforme. </p>\n\n<h3>Características: </h3>\n<p>Tira de calcanhar ajustável, tamanho único, muito leve, ambidestro.</p>\n\n<b>Fonte medi</b>', '21.00', 150, 9),
(117, 'MEDI WALKER FIXA', 'BOTA WALKER FIXA MEDI', 1, '<h3>Indicações: </h3>\n<p>Pós-ligamentoplastia e cirurgia do tendão, para tratamento pós-operatório e conservativo de fraturas da tíbia, fraturas deslocadas da articulação do calcâneo, tratamento conservativo de lesões da biqueira e centro do pé e do tarso, após refixação de ligamentos e deslocação do tendão peronial, para instabilidade ligamentosa crónica, tratamento de acompanhamento após fraturas do calcâneo maléolo medial, para terapia funcional inicial após tratamento cirúrgico conservativo para rutura do tendão de Aquiles. </p>\n\n<b>Fonte medi</b>', '117.30', 1400, 9),
(118, 'MEDI ROM WALKER', 'BOTA WALKER REGULÁVEL MEDI', 1, '<h3>Indicações: </h3>\n<p>Pós-ligamentoplastia e cirurgia do tendão, para tratamento pós-operatório e conservativo de fraturas da tíbia, fraturas deslocadas da articulação do calcâneo, tratamento conservativo de lesões da biqueira e centro do pé e do tarso, após refixação de ligamentos e deslocação do tendão peronial, para instabilidade ligamentosa crónica, tratamento de acompanhamento após fraturas do calcâneo maléolo medial, para terapia funcional inicial após tratamento cirúrgico conservativo para rutura do tendão de Aquiles. </p>\n\n<b>Fonte medi</b>', '154.90', 1401, 9),
(119, 'PROTECT.AIR WALKER BOOT', 'BOTA WALKER BOOT FIXA INSUFLÁVEL MEDI', 1, '<h3>Indicações: </h3>\n<p>Lesões ou cirurgia do pé ou dos dedos do pé, entorses do tornozelo de nível 1, 2 ou 3. Fraturas estáveis ou fixadas internamente do pé ou do tornozelo. </p>\n\n<h3>Função: </h3>\n<p>Ângulo fixo da articulação opcional ou excursão parcial, flexão plantar, flexão dorsal e imobilização ajustáveis, a sola rígida proporciona um andar natural. </p>\n\n<h3>Características: </h3>\n<p>Com controlo de flexão/extensão, o sistema de velcro facilita a inspeção de feridas e a higiene pessoal. Mais conforto e estabilização melhorada devido à bota insuflável.</p>\n\n<b>Fonte medi</b>', '88.90', 1400, 9),
(120, 'PROTECT.AIR ROM WALKER ', 'BOTA WALKER ROM REGULÁVEL INSUFLÁVEL MEDI', 1, '<h3>Indicações: </h3>\n<p>Lesões ou cirurgia do pé ou dos dedos do pé, entorses do tornozelo de nível 1, 2 ou 3. Fraturas estáveis ou fixadas internamente do pé ou do tornozelo. </p>\n\n<h3>Função: </h3> \n<p>Ângulo fixo da articulação opcional ou excursão parcial, flexão plantar, flexão dorsal e imobilização ajustáveis, a sola rígida proporciona um andar natural. </p>\n\n<h3>Características: </h3>\n<p>Com controlo de flexão/extensão, o sistema de velcro facilita a inspeção de feridas e a higiene pessoal. Mais conforto e estabilização melhorada devido à bota insuflável.</p>\n\n<b>Fonte medi</b>', '132.60', 1400, 9),
(121, 'PROTECT.CAT WALKER', 'BOTA CAT WALKER INSUFLÁVEL MEDI', 1, '<h3>Indicações: </h3>\n<p>No pós-operatório de tendão de Aquiles, ruturas ligamentares, lesões das partes moles, pós cirurgia osteoarticular. No tratamento de lesões do tendão de Aquiles, lesões ligamentares, fraturas e luxações da tibiotársica e pé e tendinopatia.</p>\n\n<p>Vantagens do produto:\nConstrução leve e sistema de ar integrado, sistema de câmara-de-ar única, para proteção otimizada e conforto. Fácil regulação da compressão através da bomba de ar integrada e válvula de ventilação, construção plana, sola arredondada para uma marcha fisiológica, sola antiderrapante, proteção da biqueira do pé ajustável e amovível. Também disponível kit de cunhas para calcanhar.</p>\n\n<b>Fonte medi</b>', '83.60', 1400, 9),
(122, 'PROTECT.WALKER BOOT', 'BOTA PROTECT WALKER BOOT FIXA MEDI', 1, '<h3>Indicações: </h3>\n<p>Lesões ou cirurgia do pé ou dos dedos do pé, entorses do tornozelo de nível 1, 2 ou 3. Fraturas estáveis ou fixadas internamente do pé ou do tornozelo. </p>\n\n<h3>Função: </h3>\n<p>Ângulo fixo da articulação opcional ou regulável, flexão plantar, flexão dorsal e imobilização ajustáveis, a sola rígida proporciona um andar natural. </p>\n\n<h3>Características: </h3>\n<p>Sem regulação (Protect Walker Boot Fixa),o sistema de velcro facilita a inspeção de feridas e a higiene pessoal.</p>\n\n<b>Fonte medi</b>', '77.06', 1400, 9),
(123, 'PROTECT.ROM WALKER', 'BOTA PROTECT WALKER ROM REGULÁVEL MEDI', 1, '<h3>Indicações: </h3>\n<p>Lesões ou cirurgia do pé ou dos dedos do pé, entorses do tornozelo de nível 1, 2 ou 3. Fraturas estáveis ou fixadas internamente do pé ou do tornozelo.</p> \n\n<h3>Função: </h3>\n<p>Ângulo fixo da articulação opcional ou regulável, flexão plantar, flexão dorsal e imobilização ajustáveis, a sola rígida proporciona um andar natural. </p>\n\n<h3>Características: </h3>\n<p>Com controlo de flexão/extensão, (Protect Rom Walker), o sistema de velcro facilita a inspeção de feridas e a higiene pessoal.</p>\n\n<b>Fonte medi</b>', '117.00', 1400, 9),
(124, 'P.620', 'TALA PARA PÉ PENDENTE RÍDIDA MEDI', 1, '<h3>Indicações: </h3>\n<p>Sequelas resultantes de paralisias flácidas ou hemiplegia provocada por AVC, limitação na zona dorsiflexão do pé, retração do tendão de Aquiles Pé pendente devido a parálise peronial. </p>\n\n<h3>Função: </h3>\n<p>Posiciona o pé a 90º, impedindo que o pé fique pendente. Mantém a postura correta durante a fase de repouso. </p>\n\n<h3>Características: </h3>\n<p>Fabricada em polipropileno de alta densidade injetado, impermeável.</p>\n\n<b>Fonte medi</b>', '32.15', 80, 9),
(125, 'BARUK', 'SAPATO BARUK ', 7, '<h3>Indicações: </h3>\n<p>Pós-cirurgia de joanetes e/ou dedos e pé. </p>\n\n<h3>Função: </h3> \n<p>Utiliza-se quando o pé está ligado com ligaduras que produzem uma imobilização tibiotársica, permitindo ao paciente exercer a carga no pé e a marcha desde a fase inicial pós-cirúrgica de forma segura para a cicatrização ocorrer sem exercer flexão sobre a zona afetada. </p>\n\n<h3>Características: </h3>\n<p>Ambidestro, base rígida, sem biqueira, sola antiderrapante e sistema de fecho de velcro. Adapta-se facilmente a pés com grande quantidade de ligaduras ou deformidades. Com tacão e frente sem base para não haver flexão dos dedos aquando da marcha do paciente.</p>', '27.90', 120, 9),
(126, 'S.P.CIRURGICO', 'SAPATO PÓS-CIRÚRGICO', 7, '<h3>Indicações: </h3>\n<p>Pós-cirurgia na zona do pé, edemas ou quando não é possível a utilização de calçado convencional. Adequado a ser utilizado com gesso. </p>\n\n<h3>Função: </h3>\n<p>Permite ao paciente fazer carga no pé e a marcha desde a fase inicial pós-cirúrgica de forma segura para a cicatrização ocorrer sem exercer flexão sobre a zona afetada. </p>\n\n<h3>Características: </h3>\n<p>Ambidestro, base rígida, sem biqueira, sola antiderrapante e sistema de fecho de velcro. Adapta-se facilmente a pés com grande quantidade de ligaduras ou deformidades.</p>', '18.25', 120, 9),
(127, 'COLLAR SOFT KIDZ', 'COLAR CERVICAL MACIO MEDI KIDZ', 1, '<h3>Indicações: </h3>\n<p>Síndrome cervical, hipermobilidade, problemas reumáticos, distensão excessiva dos músculos cervicais, torcicolos, alterações degenerativas da coluna cervical. </p>\n\n<h3>Função: </h3> \n<p>Estabilização da coluna cervical, função de aquecimento e apoio, limitação de movimentos.</p>\n\n<b>Fonte medi</b>', '19.20', 100, 10),
(130, '32BABY', 'PULSO DE CONTENÇÃO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Tendinites e bursites, traumas menores e danos secundários do pulso, ideal para a prática desportiva. </p>\n\n<h3>Características: </h3>\n<p>Compressão rapidamente ajustável, com interior de 100% algodão, extremamente confortável.</p>', '18.00', 80, 10),
(131, 'MANUMED KIDZ', 'APOIO DE PULSO COM TALA ESTABILIZADORA MEDI KIDZ', 1, '<h3>Indicações: </h3>\n<p>Artrite reumatoide, imobilização após distorção do pulso, inflamação dos tecidos moles (crónica, pós-traumática, pós-operatória, consequências secundárias de paralisias (paresia radial), tendinite, tenossinovite. </p>\n\n<h3>Função: </h3>\n<p>Imobilização e estabilização do pulso devido à tala de alumínio moldável. </p>\n\n<b>Fonte medi</b>', '31.67', 300, 10),
(132, '33SMALL', 'IMOBILIZADOR DE PUNHO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Patologias traumáticas, distorções, tendinites, rizartrose, síndrome do túnel cárpico, osteoartrose, artrose, artrite úrica aguda e artrite reumatoide. </p>\n\n<h3>Função: </h3>\n<p>Suporte do músculo tendinoso pós trauma e pós cirurgia. </p\n\n<h3>Características: </h3>\n<p>Tecnologia de fecho inovadora, com três talas anatómicas moldáveis que garantem o bloqueio completo da articulação. Prático, confortável, ambidestro, com interior de 100% algodão. </p>', '39.55', 100, 10),
(133, '34SMALL', 'IMOBILIZADOR DE POLEGAR E PUNHO PAVIS', 5, '<h3>Indicações: </h3>\n<p>Patologias traumáticas, distorções, fraturas do metacarpo, tendinite dos tendões flexores e extensores do polegar, contusões por esmagamento, artrose, rizartrose, síndrome do túnel cárpico com irradiação do polegar, osteoartrose, artrose e artrite reumatoide. </p>\n\n<h3>Função: </h3> \n<p>Suporte do tendão do músculo pós trauma e pós cirurgia aberta da primeira falange do polegar. </p>\n\n<h3>Características: </h3>\n<p>Tecnologia de fecho inovadora, com três talas anatómicas moldáveis que garantem o bloqueio completo da articulação. Prático, confortável, ambidestro, com interior de 100% algodão. </p>', '44.80', 100, 10),
(134, 'ARM SLING KIDZ', 'APOIO À ROTAÇÃO INTERNA ARM SLING MEDI KIDZ', 1, '<h3>Indicações: </h3> \n<p>Imobilização pós-operatória, deslocação do ombro. </p>\n\n<h3>Função: </h3>\n<p>Alívio da articulação do ombro, úmero e cotovelo. Fixa o braço perto do tronco, o que imobiliza o ombro.</p>\n\n<b>Fonte medi</b>', '22.78', 120, 10),
(135, 'OOP.SBPO', 'SUPORTE DE BRAÇO SIMPLES PEDIÁTRICO ORTHIA', 2, '<h3>Indicações: </h3> \n<p>Imobilização pós-operatória, pós colocação de gesso, luxações ou subluxações. </p>\n\n<h3>Função: </h3>\n<p>Faz sustentação do braço para possibilitar a recuperação.</p> \n\n<h3>Características: </h3>\n<p>Possui apoio do polegar para prevenir lesões do punho e uma alça ajustável para regular a altura do braço. </p>', '17.50', 120, 10),
(136, 'SHOULDER SLING KIDZ', 'APOIO PARA IMOBILIZAÇÃO DA ARTICULAÇÃO DO OMBRO MEDI KIDZ', 1, '<h3>Indicações: </h3>\n<p>Contusões e distensões do ombro, deslocações do ombro, lesões do manguito rotador, fraturas subcapitais estáveis do úmero e fraturas escapulares. </p>\n\n<h3>Função: </h3>\n<p>Fixação estável do ombro e da parte superior do braço, estabilização adicional graças à faixa peitoral.</p>\n\n<b>Fonte medi</b>', '39.95', 180, 10),
(137, 'OOP.SBBPO', 'SUPORTE DE BRAÇO C/BANDA PEDIÁTRICO ORTHIA', 2, '<h3>Indicações: </h3>\n<p>Imobilização após traumatismo do ombro e cotovelo, luxação e cirurgia, contusões e distorções do ombro, suporte do ombro após hemiplegia ou lesão no plexobraquial, lesões do músculo rotador, fraturas subcapitais estáveis do úmero, fraturas da omoplata, subluxações da articulação escápulo-umeral. </p>\n\n<h3>Função: </h3>\n<p>Fixação estável do ombro e da parte superior do braço, estabilização adicional graças à faixa peitoral.</p>', '32.60', 120, 10),
(138, 'EPICO ROMs KIDZ', 'ORTÓTESE DO COTOVELO MEDI KIDZ', 1, '<h3>Indicações: </h3> \n<p>Cuidados conservativos de instabilidades do cotovelo com ou sem outras lesões, cuidados ortóticos para fraturas do epicôndilo medial ou lateral, cuidados pós-operatórios após implante de prótese e após estabilização do cotovelo, tratamento funcional de seguimento para fraturas estáveis, mobilização passiva após artrólise do cotovelo, cuidados pós-operatórios de instabilidades ligamentares do cotovelo. </p>\n\n<h3>Função: </h3>\n<p>Tratamento funcional precoce individual, imobilizar a articulação do cotovelo. O movimento tem uma amplitude angular de 0º-120º na articulação, para garantir a flexibilidade de movimentos adequada à fase, através de mobilização precoce - isso aumenta a mobilidade articular e acelera o tratamento.</p>\n\n<b>Fonte medi</b>', '140.25', 350, 10),
(139, 'CLAVICLE SUPPORT KIDZ', 'SUPORTE CLAVICULAR MEDI KIDZ', 1, '<h3>Indicações: </h3>\n<p>Fraturas de clavícula, ligeira imobilização da clavícula, distensões e fraturas da clavícula e deslocações. </p>\n\n<h3>Contraindicações: </h3>\n<p>Constrição das vias respiratórias, compressão do feixe neuro vascular. </p>\n\n<h3>Função: </h3>\n<p>Fixação segura e estável da cintura escapular, estimula o alinhamento correto do osso fraturado ao puxar os ombros para trás.</p>\n\n<b>Fonte medi</b>', '41.18', 150, 10),
(140, '20BABY', 'JOELHEIRA ELÁSTICA REGULÁVEL PAVIS', 5, '<h3>Indicações: </h3>\n<p>Derrames articulares e traumas rotulianos, reabilitação avançada de patologias rotulianas. </p>\n\n<h3>Função: </h3> \n<p>Ação contentora pós-traumática, fácil ajustamento superior ou inferior à rótula, dependendo do grau de compressão desejado. </p>\n\n<h3>Características: </h3>\n<p>Ambidestro, respirável, antialérgico, 100% algodão sobre a pele.</p>', '45.50', 150, 10),
(141, 'MEDI CLASSIC KIDZ', 'TALA DEPUY PARA IMOBILIZAÇÃO DO JOELHO MEDI KIDZ', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós-operatória, estado doloroso pré-operatório, na sequência de deslocações da rótula, após lesões ligamentares. </p>\n\n<h3>Função: </h3>\n<p>Imobilização do joelho.</p>\n\n<b>Fonte medi</b>', '46.50', 250, 10),
(142, 'MEDI ROM KIDZ', 'TALA UNIVERSAL PARA JOELHO C/ LIMITAÇÃO FLEX/EXT MEDI KIDZ ', 1, '<h3>Indicações: </h3>\n<p>Imobilização pós-operatória, para imobilização após lesões do joelho e para mobilização posterior, para estabilização segura de ligamentos colaterais e/ou cruzados lesionados ou operados. </p>\n\n<h3>Função: </h3>\n<p>Estabilização colateral da articulação do joelho, imobilização pós-operatória, mobilização controlada.</p>\n\n<b>Fonte medi</b>', '107.65', 350, 10),
(143, '15BABY', 'CONTENÇÃO ELÁSTICA DO TORNOZELO COM ENFAIXAMENTO EM \"8\" PAVIS', 5, '<h3>Indicações: </h3>\n<p>Distensões, tendinites, luxações, suporte passivo para recuperação funcional do tornozelo, traumatismos do tornozelo, apoio e proteção na prática desportiva. </p>\n\n<h3>Função: </h3>\n<p>Com banda elástica que é enrolada em volta do tornozelo num formato de \"8\", como forma de limitar a abdução e adução articular.</p> \n\n<h3>Características: </h3>\n<p>Confortável, adapta-se ao pé, podendo ser facilmente utilizado em qualquer tipo de sapato, antialérgico, respirável e 100% algodão sobre a pele, ambidestra. Tamanho criança.</p>', '45.50', 120, 10),
(144, 'M.STEP KIDZ', 'TALA FUNCIONAL DO TORNOZELO COM ALMOFADA DE ESPUMA E GEL MEDI KIDZ', 1, '<h3>Indicações: </h3>\n<p>Cuidados conservativos e pós-operatórios de lesões do ligamento fibular ao nível do tornozelo, instabilidades crónicas do tornozelo, qualquer situação em que seja necessário apoio medial/lateral adicional. </p>\n\n<h3>Função: </h3>\n<p>As conchas rígidas proporcionam uma excelente estabilização em movimentos de pronação e supinação. A almofada de espuma e gel adapta-se às condições anatómicas variáveis e proporciona uma compressão uniforme.</p>\n\n<b>Fonte medi</b>', '53.82', 150, 10),
(145, 'WALKER BOOT KIDZ', 'BOTA WALKER MEDI KIDZ', 1, '<h3>Indicações: </h3>\n<p>Lesões do pé ou dos dedos, ou pós-cirurgia. Distensões do tornozelo de grau 1, 2 ou 3, fraturas estáveis, ou internamente fixas do pé ou do tornozelo. </p>\n\n<h3>Função: </h3>\n<p>Articulação fixa do tornozelo a 90º, a sola abaulada estimula a marcha natural, o sistema de velcros facilita a inspeção da ferida e a higiene pessoal.</p>\n\n<b>Fonte medi</b>', '77.06', 400, 10);
