@if(empty($shipping_methods))    <div class="alert alert-warning">        @lang('app.login-for-shipping-methods')    </div>@else    <ul class="select-list">        @foreach($shipping_methods as $method)            <li class="select-list__item clearfix">                <div class="select-list__info col-sm-8">                    <p class="text"><b>{{ $method->name }}</b> {!! (!empty($method->description)) ? '<i>(' . $method->description . ')</i>' : '' !!}</p>                </div>                <div class="select-list__selector col-sm-4">                    <p class="text select-list__price">{{ $method->formattedPrice($items_weight, $user->shippingCountry->id) }}</p>                    <span class="checkbox-btn">                        {{ Form::radio('shipping_method_id', $method->id, ($method->id == $selected_shipping_method_id)) }}                    </span>                </div>            </li>        @endforeach    </ul>@endif